export const dummyQuestions: Question[] = [{ type: "mcq", degree: 0, chapter: "", domain: "", name: "إذا أراد مدير المشروع التأكد من أن العمل المنجز والمنتج الذي يتم انشاؤه لا ينحرف عن قيمة العمل المحددة. ما الذي يجب أن يوليه مدير المشروع اهتماماً خاصاً لتحقيق هذا الهدف؟", description: "يجب ان يولى مدير المشروع اهتماماً خاصاً لمراقبة خطة تحقيق المنافع وتحديثها للتأكد من أن العمل المنجز والمنتج الذي يتم انشاؤه لاينحرف عن قيمة الأعمال المحددة.\nخطة تحقيق المنافع: وثيقة تحدد الأنشطة الضرورية لتحقيق المنافع المخططة، يحدد الجدول الزمنى والأدوات والموارد اللازمه لضمان تحقيق الفوائد بالكامل بمرمر الوقت، يحدد الفوائد والافتراضات المرتبطه بها، وكيف سيتم تحقيق كل فائدة.\nسؤال مهم للاختبار الدولى.", options: [{ option: "مراقبة هيكل تجزئة العمل (WBS)", answer: "false" }, { option: "إجراء عمليات تدقيق الجودة وتحليل الفشل", answer: "false" }, { option: "مراقبة وتحديث خطة تحقيق المنافع", answer: "true" }, { option: "مراجعة وتحديث خطة إدارة المتطلبات.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يستخدم مدير المشروع نهجاً مختلطاً لتسليم المشروع، تم تحديد حجم  فريق التطوير، ولكن لم يتم تعيين كامل فريق التطوير بعد، يعمل مدير المشروع على تحديد الجدول الزمنى للمشروع. ما الذي يجب على مدير المشروع فعله لمعالجة مسألة توفر المطور؟", description: "الجواب الصحيح هو: تنظيم جلسة تخطيط مع فريق التطوير واستخدم تقديراتهم لإكمال الجدول. يجب تقدير الأنشطة من قبل الفريق الذي يعمل على المهمة حسب سرعته لتكون أكثر دقة وواقعية.\nسؤال مهم جدا", options: [{ option: "استخدم حكم الخبراء لتقدير جميع المهام وإكمال الجدول الزمنى في الوقت المخصص.", answer: "false" }, { option: "اسأل مدير الموارد عندما يكون المقاول الجديد متاحاً للبدء وتغيير تاريخ التسليم لجدول المشروع.", answer: "false" }, { option: "اطلب من الراعى تقديم تاريخ الانتهاء المطلوب لأنشطة المشروع وإكمال الجدول الزمنى.", answer: "false" }, { option: "تنظيم جلسة تخطيط مع فريق التطوير واستخدم تقديراتهم لإكمال الجدول.", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أنت مدير لمشروع مهم، بسبب مشكلة فنيه مع المورد الفرعى، هناك تأخير محتمل في التسليم. للبقاء في الموعد المحدد، اقترح البائع استبدال مكون المورد الفرعى بجزء مشابه يختلف عن الجزء المنصوص عليه فى مواصفات المشروع.\u000bبصفتك مدير المشروع، ماذا يجب أن تفعل بعد ذلك؟", description: "الكلمه المفتاحية ( ماذا يجب أن تفعل \"أول خطوة\")\nيجب على مدير المشروع التشاور مع خبير فى الموضوع لمعرفة ما إذا كان التغيير المقترح مقبولا ام لا، ثم يمكنه اتخاذ القرار الصحيح.\nإذا كان التغيير المقترح غير مقبول، فيجب على مدير المشروع أن يوجه البائع لاستخدام الجزء المتوافق مع مواصفات المشروع.\nسؤال مهم جدا للاختبار الحقيقى.", options: [{ option: "قم بإرشاد البائع لاستخدام الجزء المتوافق مع مواصفات المشروع وتحديث الجدول الزمنى.", answer: "false" }, { option: "قبول الاستبدال المقترح من اجل الحفاظ على أنشطة الشراء فى المسار الصحيح.", answer: "false" }, { option: "استشر خبيراً فى الموضوع لمعرفة ما إذا كان التغيير المقترح مقبولاً.", answer: "true" }, { option: "الإصرار عل استخدام المكون حسب مواصفات المشروع والشحن الجوى للمعدات لتخفيف التأخير.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أثناء جمع المتطلبات وتحديد منتج المشروع، قمت بتعيين استشارى خارجى لتقديم المشورة لك حول كيفية القيام بذلك بشكل أفضل ورفع مستوى الأساليب المرتبطة بها. الاستشاري لديه معرفه واسعة بأفضل الممارسات في الصناعة، ما الذي سيفعله الاستشارى بشكل أساسي؟", description: "بما أن الاستشارى قد تم تعيينه للحصول على مشورة بشأن التحسين، فإن قياس الأداء من خلال التوصية بأفضل الممارسات في الصناعة وجمع المتطلبات وتحديد المنتجات.", options: [{ option: "تبادل الافكار بين اعضاء فريقك لتحديد أفضل الممارسات لجمع المتطلبات وتعريف المنتج.", answer: "false" }, { option: "يعلم فريق المشروع على تقنيات تحليل المتطلبات.", answer: "false" }, { option: "اجراء مقارنات الاداء من خلال التوصية بأفضل الممارسات فى الصناعة وجمع المتطلبات وتحديد المنتجات.", answer: "true" }, { option: "إدارة استبيان مع المعنيين الرئيسيين فى مشروعك حول أفضل ممارساتهم", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أنت جزء من فريق المشروع الذي يصمم نوعاً جديداً من عربات مترو الانفاق، لقد اكملت حزمة العمل الأخيرة لنظام الفرامل، ومع ذلك، فأنت غير متأكد من معايير القبول لحزمة العمل هذه. اين يمكنك ان تجد تلك المعلومات؟", description: "قاموس هيكل تجزئة  العمل عبارة عن وثيقة توفر معلومات تفصيلية حول التسليمات والنشاط والجدولة الزمنية بشأن كل مكون في هيكل تجزئة العمل. ويعد هذا القاموس وثيقة تدعم هيكل تجزئة العمل، ومعظم المعلومات المدرجة في قاموس هيكل تجزئة العمل أنشئت من خلال عمليات اخرى وأضيفت إلى هذه الوثيقة في مرحلة لاحقة كتاب البمبوك الاصدار السادس- صفحة رقم 162.", options: [{ option: "قاموس هيكل تجزئة العمل  WBS", answer: "true" }, { option: "وثائق المتطلبات", answer: "false" }, { option: "خطة ادارة المشروع", answer: "false" }, { option: "بيان نطاق المشروع", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يعمل مدير المشروع في إدارة المخاطر وإنشاء سجل المخاطر، سجل المخاطر يشتمل على تفاصيل جميع المخاطر التى تم تحديدها في بداية وأثناء عمر المشروع. ما الأداة او التقنية التى يجب على مدير المشروع استخدامها لإنشاء قائمة بجميع مخاطر المشروع المحتملة؟", description: "العصف الذهنى هو أسلوب لجمع البيانات يمكن استخدامه لتحديد عملية المخاطر والهدف من العصف الذهنى هو الحصول على قائمة شاملة بمخاطر المشروع الفردية ومصادر المخاطر الكلية للمشروع.", options: [{ option: "مصفوفة الاحتمالات والتأثير", answer: "false" }, { option: "المخطط الاعصارى- تورنادو", answer: "false" }, { option: "مونت كارلو", answer: "false" }, { option: "العصف الذهني", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "توقف بناء مصنع لمدة اربعة أشهر بسبب المحظورات الخاصة بكل منطقة، ما الذي يجب على مدير المشروع فعله لتجنب هذه المشكلة؟", description: "الكلمة الرئيسية (بسبب المحظورات الخاصة بالمنطقة) ... من الواضح أن هذا البلد لديه محظورات ... يجب على مدير المشروع تحديد هذا على أنه خطر في سجل المخاطر وتحليل المخاطر والتخطيط للاستجابة للمخاطرلتجنب أو تخفيف هذا الخطر.", options: [{ option: "تحديد هذا على أنه خطر في سجل المخاطر", answer: "true" }, { option: "توثيق جميع الافتراضات", answer: "false" }, { option: "ضغط الجدول الزمني للمشروع", answer: "false" }, { option: "حدد قائمة أولويات قيود المشروع", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أنت تدير مشروعًا من المقرر إصدار مستند تصميم الأسبوع المقبل. تقوم بمراجعة المستند ، عندما يطلب منك صاحب مصلحة مؤثر إضافة ميزة إلى التصميم الحالي. في وقت سابق، حاول صاحب المصلحة هذا إزعاجك بمطالب غير معقولة خلال الأيام الأخيرة من الإصدار ما هو أفضل ما يمكنك فعله في هذه الحالة؟", description: "من المهم أن ينظر مدير المشروع في طلبات التغيير المكتوبة والموثقة جيدا للمشروع", options: [{ option: "نظرًا لأن هذا الشخص مؤثر، فليس لديك خيار سوى الاستماع إليه وإضافة الوظيفة", answer: "false" }, { option: "حدد تأثير هذا التغيير على جدولك الزمني والقيود قبل اتخاذ القرار", answer: "false" }, { option: "اطلب منه رفع نموذج طلب تغيير كتابي ثم اتباع التحكم المتكامل في التغيير", answer: "true" }, { option: "أبلغ عن سلوكه إلى كبير المديرين ، مع اقتراح سبب عدم ارتباطه بمشروعك كصاحب مصلحة", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أحد الشركات لديها مشكلات جودة متكررة مع أحد مورديها الوحيدين سيتعرض المشروع الجديد لعقوبات كبيرة من العميل إذا تأخرت التسليمات. ماذا يجب أن يفعل مدير المشروع؟", description: "في (المشاريع السابقة)، واجهت الشركة مشكلات جودة متكررة مع أحد مورديها الوحيدين. في المشروع الجديد سوف تتحمل عقوبات كبيرة من العميل (((إذا))) التسليم تأخر، لذا فهذه مخاطرة عالية المستوى ويجب أن تؤخذ في الاعتبار في البداية، ثم في مرحلة التخطيط، مدير المشروع يجب أن تجري خطة تخفيف لتقليل هذه المخاطر مثل إجراء المزيد من الاختبارات ومراجعة تقارير التفتيش الواردة وسجلات الجودة نظرًا لأن مصدرها الوحيد . فليس لديك أي خيار آخر للتفاوض مع البائعين الآخرين باستخدام أساليب أفضل لمراقبة الجودة. لذلك لديك للنظر في هذه المخاطر أثناء التخطيط للمشروع، حيث يجب أن تكون استباقيًا، لذا فإن عملية التصعيد إلى الإدارة العليا هي القرار الأخير.", options: [{ option: "تصعيد المشكلة إلى الإدارة العليا", answer: "false" }, { option: "مراجعة تقارير الفحص الواردة وسجلات الجودة", answer: "false" }, { option: "تفاوض مع البائعين الآخرين باستخدام طرق أفضل لمراقبة الجودة", answer: "false" }, { option: "تحديد المخاطر عالية المستوى قبل وضع خطط الطوارئ", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أنت مدير مشروع برمجيات. أكمل فريق التطوير المشروع ويخطط لبدء مرحلة الإغلاق. ومع ذلك، يرفض أحد المعنيين الرئيسيين التوقيع بسبب عدم الامتثال للنتائج المتفق عليها. ما الذي يجب على مدير المشروع فعله لتسهيل القبول؟", description: "تحليل التباين هو أحد الادوات والاساليب في عملية إغلاق المشروع أو المرحلة يجب على مدير المشروع إجراء تحليل التباين لتحديد أي انحراف في المخرجات من خلال مقارنة ما تم التخطيط له في البداية والنتيجة النهائية.", options: [{ option: "تسجيل عدم الامتثال وابدأ في إغلاق المشروع.", answer: "false" }, { option: "تقديم بيان عمل جديدًا يشمل التباين في التسليمات.", answer: "false" }, { option: "إجراء تحليل التباين لتحديد أي انحراف في التسليمات.", answer: "true" }, { option: "توثيق عدم الامتثال وابدأ عملية إدارة التغيير.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يقوم مشروع رشيق بتشغيل أنشطة لتحديد الحد الأدنى من المنتج القابل للتطبيق (MVP) أثناء الجلسة ، يحدد مدير المشروع بعض اللوائح الإلزامية ، ولكن لا يوجد إجماع على تضمين هذه اللوائح في MVP لأنه إذا كان من الممكن تمديد مدة المشروع. ماذا يجب أن يفعل مدير المشروع؟", description: "من أجل تحديد منتج قابل للاستخدام للعميل في كل زيادة ، يجب فهم القيمة الأساسية بناءً على القيمة الأساسية التي يسعى إليها المشروع، يتم تحديد الحد الأدنى من كيفية تحقيق القيمة نظرًا لوجود لوائح إلزامية يجب تضمينها ، يجب على مدير المشروع الحصول على التزام من الفريق لتضمين جميع اللوائح المطلوبة.", options: [{ option: "توجيه المشاركين بضرورة التركيز فقط على وظائف المنتج.", answer: "false" }, { option: "الحصول على التزام من الفريق بإدراج جميع اللوائح المطلوبة.", answer: "true" }, { option: "اطلب من راعي المشروع إضافة المزيد من الوقت إلى المشروع.", answer: "false" }, { option: "تدريب الفريق على اللوائح الجديدة حسب طلب الإدارة.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "عند تقديم أساليب Agile إلى مؤسسة أدارت تاريخياً جميع مشاريعها باستخدام دورات الحياة التنبؤية، أي من الأساليب التالية من المرجح أن تنجح؟", description: "يُنصح بتجربة الأساليب الجديدة في مشروع أقل خطورة بدرجة متوسطة إلى منخفضة من عدم اليقين. كلما انخفضت المخاطر، زادت فرص النجاح أنت بحاجة إلى بعض المكاسب السريعة للمساعدة في الانتقال السلس إلى الأساليب الجديدة. [دليل الممارسة الرشيقة ، الطبعة الأولى ، الصفحة 30]", options: [{ option: "تجربة التقنيات الجديدة في المشاريع المنجزة.", answer: "false" }, { option: "تجربة التقنيات الجديدة في المشاريع عالية القيمة.", answer: "false" }, { option: "تجربة التقنيات الجديدة في مشروع تجريبي أقل خطورة.", answer: "true" }, { option: "تجربة التقنيات الجديدة في المشاريع الجارية.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يجب أن يتمتع مدير المشروع الماهر بالخبرة اللازمة لتحديد مناهج إدارة المشروع المناسبة التقليدية مقابل الرشيقة ( وتصميم الأدوات والتقنيات والأساليب لكل مشروع. ماذا تسمى هذه المهارات؟", description: "هذا مثال على مهارات الإدارة الفنية للمشاريع", options: [{ option: "المهارات الشخصية", answer: "false" }, { option: "مهارات الإدارة الاستراتيجية وادارة الأعمال", answer: "false" }, { option: "مهارات القيادة", answer: "false" }, { option: "مهارات الإدارة الفنية للمشاريع", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "تقوم مونيكا ، مديرة مشروع الأجهزة، بإجراء مراجعة منظمة لعملية الشراء. تود فحص كل وثيقة من عملية إدارة المشتريات من خلال عملية مراقبة المشتريات لتحديد حالات الفشل. ما الأداة التي يجب أن تسخدمها مونيكا ؟", description: "تدقيق المشتريات هو أداة في عملية التحكم فى المشتريات. يتم إجراء عمليات تدقيق المشتريات لتحديد حالات النجاح والفشل التي تتطلب الاعتراف عند استخدام هذه الأداة ، يتم فحص جميع عمليات الشراء بدقة ، ويتم فحص المستندات المقابلة بحثًا عن المشاكل. لذلك ، فإن الإجابة الصحيحة هي عمليات تدقيق المشتريات في عملية مراقبة المشتريات", options: [{ option: "التسويات التفاوضية في عملية مشتريات المراقبة", answer: "false" }, { option: "تقارير الأداء في عملية تنفيذ المشتريات", answer: "false" }, { option: "تدقيق المشتريات في عملية تنفيذ المشتريات", answer: "false" }, { option: "تدقيق المشتريات في عملية التحكم في المشتريات", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "خلال اجتماع الوقوف اليومي، ذكرت إحدى المبرمجين أن لديها بعض الوقت الإضافي والوظائف الإضافية، والتي لم يتم تضمينها في التصميم الأصلي. إنها تعتقد أن العميل سيقدر الوظيفة الإضافية. ما هو أفضل إجراء يجب عليك القيام به ؟", description: "يجب إزالة رمز البرنامج المضاف ، ويجب على مدير المشروع التأكد من كتابة الكود كما هو مخطط له في المستقبل. ستؤدي إزالة رمز البرنامج الإضافي إلى تقليل مخاطر المشروع الإجمالية المقدمة مع الوظائف الإضافية.", options: [{ option: "قم بتحديث خط الأساس للتكلفة حيث سيتم تشغيل المشروع فوق الميزانية بسبب تكلفة إضافة وظائف جديدة", answer: "true" }, { option: "حذف الوظائق المضافة وتأكد من أن فريق المشروع يبني البرنامج على النحو المصمم", answer: "false" }, { option: "اشكر المبرمج على المجهود الإضافي وأبلغ العميل بالوظيفة الجديدة التي تمت إضافتها", answer: "false" }, { option: "قبول الوظيفة المضافة ، لكن اطلب من المبرمج بناء ما تم تصميمه فقط للمضي قدمًا", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أثناء أداء العمل في المشروع، يلاحظ أحد أعضاء الفريق وجود مخاطر جديدة محتملة، والتي يمكن أن تؤثر على المسار الحرج للمشروع. ماذا يجب أن يفعل عضو الفريق على الفور؟", description: "يعمل سجل المخاطر كمستودع يتم فيه تسجيل المخاطر المحددة، بما في ذلك مخرجات عمليات إدارة المخاطر الأخرى. الخطوة الأولى الفورية التي يجب أن يتخذها عضو الفريق هي توثيق المخاطر الجديدة في سجل المخاطر حتى لا يتم نسيانها. يجب على عضو الفريق بعد ذلك استشارة خطة إدارة مخاطر المشروع للحصول على معلومات حول ما يجب القيام به بعد ذلك", options: [{ option: "يوصي بتعليق المشروع لمدة يوم لتجنب المخاطر", answer: "false" }, { option: "ضع مسودة لخطة احتياطية طارئة لأن المسار الحرج في خطر", answer: "false" }, { option: "قم بإنشاء تقرير مخاطر باستخدام التحليل النوعى للمخاطر المحددة لتنبيه الفريق بآثار الجدول الزمني", answer: "false" }, { option: "توثيق الخطر في سجل المخاطر", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "انتهى فريقك من تحديد المخاطر التي تواجه مشروعك ويقوم الآن بتحليل جودة بيانات المخاطر. ماذا سوف نفعل بعد؟", description: "بعد تحديد المخاطر وتقييم جودة بيانات المخاطر، يجب على فريق المشروع مناقشة احتمالية وتأثير المخاطر المحددة، وتحديد مستوى الأولوية باستخدام مصفوفة الاحتمالات والأثر - وهي تقنية مستخدمة في عملية إجراء التحليل النوعي من خلال استكمال هذه الخطوة، يمكن لفريق المشروع تحديد أي من المخاطر المحددة لها الأولوية القصوى وتتطلب مزيدًا من التحليل والإجراءات", options: [{ option: "إجراء تحليل الاحتياطي", answer: "false" }, { option: "إنشاء خطة استجابة", answer: "false" }, { option: "تشغيل محاكاة مونت كارلو", answer: "false" }, { option: "تطبيق مصفوفة الاحتمالات والتأثير", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أثناء مرحلة التخطيط ، يعلم مدير المشروع أن الراعي قد ألغى بعض الإنجازات المهمة في مشاريع أخرى على مدار السنوات الثلاث الماضية ، مما أدى إلى تباين سلبي في التكلفة. ماذا يجب أن يفعل مدير المشروع بعد ذلك؟", description: "في المشروع السابق ، ألغى راعي المشروع بعض الإنجازات المهمة مما تسبب في تباين سلبي في التكلفة. هذا يأخذ في الاعتبار المخاطر ويجب إدراجها في سجل المخاطر.", options: [{ option: "توثيق المشكلة في سجل الاشكالات", answer: "false" }, { option: "تسجيل الخطر في سجل المخاطر.", answer: "true" }, { option: "توثيق الدروس المستفادة", answer: "false" }, { option: "تحديد النطاق الأساسي بشكل صحيح.", answer: "false" }] }, { type: "dragdrop", degree: 0, chapter: "", domain: "", name: "أنت مدير لمشروع بناء حدثت العديد من مشكلات الجدول. اسحب مشكلات الجدول على اليسار إلى الأدوات المحددة الموجودة على اليمين والتي إذا تم تنفيذها أثناء تطوير الجدول الزمنى ، كان من الممكن ان تمنع حدوث المشكلات", description: "\u000bالتقدير باستخدام المعاملات يستخدم لتقدير مدة النشاط. يستخدم التحليل لتحليل حزم العمل إلى أنشطة أصغر لتجنب الأنشطة المفقودة. طريقة طريقة التخطيط التتابعي (PDM) هي تقنية مستخدمة لإنشاء نموذج جدول يتم فيه تمثيل الأنشطة بالعقد وترتبط بيانياً بواحدة أو أكثر من العلاقات المنطقية لإظهار التسلسل الذي سيتم تنفيذ الأنشطة فيه. تُستخدم طريقة المسار الحرج لتقدير الحد الأدنى لمدة المشروع وتحديد مقدار مرونة الجدول الزمني على مسارات الشبكة المنطقية ضمن نموذج الجدول الزمني ويمكن تجنب تبعيات النشاط غير المكتملة أو غير الصحيحة. (1-ا * 2-ب * 3-ج * 4-د)", options: [{ option: "ا- مدة النشاط غير الواقعية", answer: "2- التجزئة" }, { option: "ب- الأنشطة المفقودة", answer: "3- تسلسل غير صحيح" }, { option: "ج- اعتماديات النشاط غير كاملة او خاطئة", answer: "4- طريقة المسار الحرج" }, { option: "د- طريقة التخطيط التتابعي", answer: "1- تقييم بارامتری" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "تم إيقاف مرحلة الإنتاج لأن محقق الجودة ادعى أن الملصقات التنظيمية مفقودة. كمدير مشروع ، ماذا يجب أن تفعل؟", description: "يجب على مدير المشروع مراجعة خطة إدارة الجودة لأنها تتضمن الإجراءات الرئيسية ذات الصلة بالمشروع، مثل التعامل مع عدم المطابقة، وإجراءات الإجراءات التصحيحية ، وإجراءات التحسين المستمر.", options: [{ option: "تحديث سجل الدروس المستفادة", answer: "false" }, { option: "مراجعة خطة إدارة الجودة", answer: "true" }, { option: "مراجعة خطة إدارة المخاطر", answer: "false" }, { option: "إجراء تقييم من قبل خبير متخصص", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أي من الطرق التالية توفر إرشادات حول استخدام سجل اعمال المنتج ، اجتماع التخطيط ، واجتماع اسكرم اليومي ، واجتماع الدروس المستفادة واجتماع المراجعة بعد التكرار؟", description: "توفر طريقة سكروم إرشادات حول حول استخدام سجل اعمال المنتج ، اجتماع التخطيط ، واجتماع اسكرم اليومي، واجتماع الدروس المستفادة واجتماع المراجعة بعد التكرار.", options: [{ option: "اکس بی XP", answer: "false" }, { option: "التكرار Sprint", answer: "false" }, { option: "کانبان Kanban", answer: "false" }, { option: "اسکرم Scrum", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يشعر أحد أصحاب المصلحة الداخليين بالانزعاج لأنه لم يتم تضمينه قبل بدء المشروع. يطلب صاحب المصلحة إضافة إلى مصفوفة تتبع المتطلبات. بصفتك مدير مشروع، ما الذي يجب عليك فعله لتحسين العلاقة معه ؟", description: "يجب على مدير المشروع تحديد صاحب المصلحة أولاً. لذلك يجب عليه إشراك صاحب المصلحة للحصول على مزيد من المعلومات وتحديث سجل أصحاب المصلحة الخيار تحديث خطة إشراك أصحاب المصلحة لضمان إدراج أصحاب المصلحة في قائمة أصحاب المصلحة في المشروع. غير صحيح لأن الخطة لم تتضمن قائمة أصحاب المصلحة الخيار إصدار طلب التغيير بحيث تغيير لوحة التحكم (CCB)غير صحيح لأن تحديث سجل (SH) لا يحتاج إلى طلب تغيير معتمد ، وثيقة المعيشة الخاصة به.", options: [{ option: "قم بإشراكه للحصول على مزيد من المعلومات.", answer: "true" }, { option: "ارسال طلب تغيير الى لجنة التحكم في التغيير", answer: "false" }, { option: "راجع مستند نطاق المشروع للتأكد من أن الإضافة في النطاق.", answer: "false" }, { option: "قم بتحديث خطة مشاركة المعنيين لضمان إدراج صاحب المصلحة في سجل المعنيين في المشروع.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يواجه فريق المشروع تأخيرات في إكمال مهمة تبين أنها أكثر تعقيدًا مما كان متوقعًا في البداية. هذه مهمة حاسمة يمكن أن تؤثر على أهداف التكرار الحالية. ما الخياران اللذان سيساعدان الفريق في معالجة هذه العقبة في البداية؟ إختر إثنين", description: "افضل خيارين للتعامل مع المشكلة مبدئيا :\n1- تطبيق تحليل السبب الجذري\n2- مراجعة وتحديث الاعتماديات", options: [{ option: "تطبيق تحليل السبب الجذري", answer: "true" }, { option: "مراجعة وتحديث الاعتماديات", answer: "true" }, { option: "تحديد اجتماع تقييم باثر راجعي", answer: "false" }, { option: "تطوير خطة إدارة المخاطر", answer: "false" }, { option: "تصعيد المشكلة الى راعى المشروع", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "مدير مشروع في الشركة يعتبر ان عضو الفريق ذو الاداء العالى يمكنه ان يكون مدير مشروع للمشروع الحالي. ما الذي ينبغى ان يجهزه مدير المشروع لتوصيه عند الادارة ؟", description: "يجب على مدير المشروع جمع أدوات المشروع التي توضح الأداء العالي لعضو الفريق للتحضير لتوصية للإدارة العليا.", options: [{ option: "يجمع أدوات المشروع التي تُظهر الأداء العالي لعضو الفريق", answer: "true" }, { option: "حساب صحة المشاريع التي عمل عليها عضو الفريق", answer: "false" }, { option: "يقوم بتجميع النقاط البارزة الرئيسية التي تظهر عضو الفريق من محضر الاجتماع", answer: "false" }, { option: "يطلب من عضو الفريق كتابة سيرة ذاتية مختصرة تسرد مؤهلاته", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "انت مدير المشروع تنفيذ مشروع الفواتير الإلكترونية الذي اكتمل للتو.\u000bأبلغ المدير المالي أن الفريق اكتشف ثلاث فواتير بها أخطاء. ما الوثيقتان اللتان يجب على مدير المشروع تحديثهما في أسرع وقت ممكن؟ (إختر إثنين)", description: "يجب على مدير المشروع تحديث سجل المشكلات لتسجيل الأخطاء وتحديث سجل المخاطر لأنه قد يؤثر على تاريخ اكتمال المشروع.", options: [{ option: "سجل اعمال المنتج", answer: "false" }, { option: "سجل الاشكالات", answer: "true" }, { option: "سجل المعنيين", answer: "false" }, { option: "سجل المخاطر", answer: "true" }, { option: "سجل التغيير", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يقوم فريق المشروع بإشراك فريق تطوير في بلد آخر لتطوير البرمجيات أثناء مراجعات التقدم ، حدد مدير المشروع تأخيرات في تاريخ الإنجاز بسبب إعادة العمل نظرًا لوجود تفسيرات مختلفة لاحتياجات العميل. ماذا يجب أن يفعل مدير المشروع؟", description: "يقوم باعداد اجتماع مع أصحاب المصلحة الرئيسيين لتوضيح المتطلبات", options: [{ option: "السفر إلى مكتب فريق التطوير لدفع الجدول الزمني", answer: "false" }, { option: "يقوم بإنشاء نماذج أولية مع فريق التطوير لتأكيد المتطلبات", answer: "false" }, { option: "يقوم بإعداد اجتماع مع أصحاب المصلحة الرئيسيين لتوضيح المتطلبات", answer: "true" }, { option: "تصعيد إلى اللجنة التوجيهية للمشروع واطلب وقتًا إضافيًا", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أنت مدير لمشروع مهم تأخر المشروع بسبب الكم الهائل من التغييرات المطلوبة من قبل مختلف أصحاب المصلحة في مراحل مختلفة من المشروع.\u000bإذا استمر المشروع على نفس المسار، فسيستهلك ضعف الوقت والميزانية المخططة. ما الإجراءان اللذان يمكن لمدير المشروع اتخاذه ليكون أكثر فعالية من حيث التكلفة؟ (إختر إثنين)", description: "الكلمة المفتاحية (قدر كبير من التغييرات مطلوبة من قبل مختلف أصحاب المصلحة) لذلك فإن النهج التدريجي هو الأفضل لهذا المشروع.\nأيضًا ، يجب على مدير المشروع إجراء تحليل لأصحاب المصلحة لتحديد: مصالح جميع أصحاب المصلحة ، الذين قد يؤثرون أو يتأثرون بالمشروع المجموعات التي يجب تشجيعها للمشاركة في مراحل المشروع المختلفة. طرق لتقليل الآثار السلبية المحتملة وإدارة أصحاب المصلحة السلبيين.", options: [{ option: "اعتماد نهج تدريجي", answer: "true" }, { option: "تحديث خطة إدارة المشروع مع الجدول الزمني الجديد", answer: "false" }, { option: "تحليل المعنيين", answer: "true" }, { option: "عزز تواصل الفريق", answer: "false" }, { option: "تقسيم العمل بين اعضاء الفريق", answer: "false" }] }, { type: "dragdrop", degree: 0, chapter: "", domain: "", name: "يشعر مدير المشروع بالقلق من أن أحد أصحاب المصلحة الرئيسيين لا يشارك في المشروع وافاد إلى أن المنتج النهائي ليس جيدًا كما كان يمكن أن يكون لو كان صاحب المصلحة أكثر مشاركة اسحب السيناريو الموجود على اليسار إلى استراتيجية الاستجابة الصحيحة للمخاطر على اليمين", description: "", options: [{ option: "a-حلل ما إذا كان يمكن لشخص آخر المساهمة", answer: "2-القبول" }, { option: "b-سجل الملاحظات والمضي قدما", answer: "3-التجنب" }, { option: "c-تحقق من وجود خيارات لاستبعاد أصحاب المصلحة", answer: "4-التصعيد" }, { option: "d-عالج المشكلة مع مدير أصحاب المصلحة", answer: "5-التخفيف" }, { option: "e-ناقش مع أصحاب المصلحة واكسب مشاركتهم", answer: "1-التحويل" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "بدأ المشروع منذ 11 شهرًا وهو حاليا قيد التكرار 12 من 15 وقد أدى الفريق أداءً جيدًا وأكمل جميع الإنجازات في الوقت المحدد ومع ذلك ، بدأ بعض أعضاء الفريق في الشكوى من طول مدة المشروع، مما يشكل خطرًا على قلة الأداء في هذه المرحلة الحرجة ما الذي يجب أن يفعله مدير المشروع كقائد خادم للتخفيف من هذه المخاطر؟", description: "حدد موعدًا لحدث جماعي مع راعي المشروع لتسليط الضوء على أهمية المشروع وتعزيز عمل الفريق.", options: [{ option: "تطبيق ضوابط إضافية على مخرجات المشروع لضمان تحقيق جميع المعالم في الوقت المحدد.", answer: "false" }, { option: "حدد موعدًا لحدث جماعي مع راعي المشروع لتسليط الضوء على أهمية المشروع وتعزيز عمل الفريق.", answer: "true" }, { option: "اتصل بالمدير الوظيفي للتأكد من أن تحفيز الفريق يظل مرتفعًا حتى نهاية المشروع.", answer: "false" }, { option: "اجتمع مع الفريق لتعزيز مسؤولياتهم والعواقب إذا لم تكتمل مخرجات المشروع في الوقت المحدد.", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "تستخدم الشركة نهجًا تنبئيًا لتطوير مكون معين على النحو المحدد في خطة إدارة النطاق. نظرًا للتغير المنتظم للمتطلبات التنظيمية ، فقد طلب فريق التطوير استخدام مناهج رشيقة. ما الذي يجب على مدير المشروع فعله بشأن طلب فريق التطوير؟", description: "تحليل المتطلبات التي سوف تحتاج إلى معالجتها في إطار الطريقة المطلوبة ثم بعد ذلك إحالة الطلب إلى مكتب إدارة المشروع لتعديل أصول العملية التنظيمية", options: [{ option: "تحليل المتطلبات التي سوف تحتاج إلى معالجتها في إطار الطريقة المطلوبة", answer: "true" }, { option: "تصعيد طلب الموارد المالية الإضافية", answer: "false" }, { option: "إحالة الطلب إلى مكتب إدارة المشروع (PMO) لتعديل أصول العملية التنظيمية", answer: "false" }, { option: "اطلب من راعي المشروع الحصول على موافقة لتنفيذ النهج الجديد", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يدعو مدير المشروع الرئيس التنفيذي للشركة والراعي وأعضاء الفريق وممثل العميل الخارجي إلى اجتماع لمراجعة خطة إدارة المشروع يبدأ مدير المشروع في عرض الميزانية لكن الرئيس التنفيذي يطلب فجأة إنهاء الاجتماع ما الذي كان يجب على مدير المشروع فعله لتجنب هذا الموقف؟", description: "مراجعة خطة إدارة الاتصالات قبل دعوة ممثل العميل الخارجي إلى الاجتماع.\nتتضمن خطة إدارة الاتصالات معلومات للتواصل ، أو الشخص أو المجموعات التي ستتلقى المعلومات ، بما في ذلك معلومات حول احتياجاتهم ومتطلباتهم وتوقعاتهم :", options: [{ option: "أجرى تحليل السبب الجذري مع ممثل العميل الخارجي ووثق النتائج في سجل الدروس المستفادة", answer: "false" }, { option: "أرسل شرائح العرض التقديمي إلى المشاركين الداخليين أولاً ثم إلى ممثل العميل الخارجي قبل الاجتماع", answer: "false" }, { option: "مراجعة خطة إدارة التكلفة مع المشاركين الداخليين والمدير التنفيذي قبل دعوة ممثل العميل الخارجي إلى الاجتماع", answer: "false" }, { option: "مراجعة خطة إدارة الاتصالات قبل دعوة ممثل العميل الخارجي إلى الاجتماع", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يلاحظ مدير مشروع جديد أنه من المحتمل أن يتم تفويت الموعد النهائي للمشروع بسبب حدوث بعض التأخير في المشروع بسبب نقص الموارد. كمدير مشروع ، ماذا يجب أن تفعل بعد ذلك؟", description: "الكلمة المفتاحية ( مدير مشروع جديد ) لذا الخطوة الأولى ، يجب على مدير المشروع تنظيم اجتماع للفريق لمناقشة مسار العمل التالي.", options: [{ option: "تصعيد المشكلة إلى الراعي واستخلاص المعلومات منه بشأن الموقف", answer: "false" }, { option: "تنظيم اجتماع للفريق لمناقشة مسار العمل التالي", answer: "true" }, { option: "راجع قائمة الأحداث الرئيسية لتحديد المهام التي يمكن تتبعها بسرعة", answer: "false" }, { option: "مراجعة خطة إدارة المخاطر لتحديد استراتيجية الاستجابة", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: " يريد أحد أصحاب المصلحة الرئيسيين أن يفهم متى قد تصبح المخاطر مشكلة . ما هي القطع الأثرية التي يجب على مدير المشروع مشاركتها مع صاحب المصلحة؟", description: "سجل المخاطر هو الجواب الصحيح لأنه يشمل: \nمحدد المخاطر.                                                         تأثير..\nمعرّف عددي لكل خطر.                                              نتيجة..\nوصف المخاطر.                                                       ملكية الخطر..\nوصف نصي لكل خطر..                                             تخفيف المخاطر..\nالاشارة التحذيرية للحظر التي تسبب حدوث الخطر.              الاستجابات\nاحتمالا. تقييم احتمالية الخطر.", options: [{ option: "خطة إدارة المخاطر", answer: "false" }, { option: "سجل المخاطر", answer: "true" }, { option: "خطة إدارة المشروع", answer: "false" }, { option: "سجل الاشكالات", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "لدى مدير المشروع فريق من الموارد المشتركة من مشاريع أخرى تعمل في مشروع عميل ثلاثة أيام قبل موعد تسليم المشروع. يقوم الفريق بإبلاغ مدير المشروع أن المشروع سوف يتأخر لأن أحد المكونات لم يتم تسليمه في الوقت المحدد تؤدي الأولويات المتنافسة للموارد المشتركة إلى تأخير تسليم المكون. ما الذي كان يجب على مدير المشروع فعله لمنع حدوث هذا التأخير؟", description: "الاجابه الصحيحة هي :\nالحفاظ على التواصل المستمر مع أصحاب المصلحة في المشاريع الأخرى", options: [{ option: "الحفاظ على التواصل المستمر مع أصحاب المصلحة في المشاريع الأخرى", answer: "true" }, { option: "إعادة تنظيم الفريق للمشروع بناءً على هيكل تجزئة العمل (((WBS)", answer: "false" }, { option: "تحديث خطة إدارة المخاطر وتسجيل الدروس المستفادة من المشروع", answer: "false" }, { option: "استخدم خطة إدارة الموارد لتحديد تأثيرات المشاريع الأخرى", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "انت مدير مشروع بملايين الدولارات. يعتبر المشروع عالي المخاطر حيث لم يتم تنفيذ مشاريع مماثلة من قبل ولا توجد بيانات تاريخية يحجم بعض أصحاب المصلحة الخارجيين عن تقديم الدعم. كمدير مشروع ما هو النهج الذي يجب أن تتبعه ؟", description: "نظرا لعدم وجود مشاريع مماثلة تم تنفيذها من قبل مثل هذا المشروع ولا توجد بيانات تاريخية، يجب على مدير المشروع إقامة حوار مع أصحاب المصلحة حول قيود المشروع والافتراضات والمدخلات الهامة لتقليل المخاطر وكسب التزام أصحاب المصلحة بـ دعم المشروع.", options: [{ option: "إقامة حوار مع أصحاب المصلحة حول قيود المشروع والافتراضات والمدخلات الهامة", answer: "true" }, { option: "إقناع الإدارة العليا بإغلاق المشروع لأن المشروع يفتقر للرعاية ويعتبر عالي الخطورة", answer: "false" }, { option: "فحص الخطط والعمليات والسياسات والمعرفة الخاصة بالمنظمة التي تتولى المشروع", answer: "false" }, { option: "الاجتماع مع فريق المشروع ، ثم قم بتحديث سجل المخاطر وخطة إدارة المشروع", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أثناء تنفيذ المشروع ، يراجع مدير المشروع تقدم المشروع ويجد بعض المهام التي تأخرت عن موعدها. بعد التحقيق، اكتشف مدير المشروع أنه تم سحب عدد قليل من أعضاء الفريق للعمل في مهام أخرى ، مما أثر على قدرتهم على التركيز على مشروع مدير المشروع. ما الإجراءان اللذان يمكن لمدير المشروع اتخاذه لإعادة الفريق إلى المسار الصحيح؟ (إختر إثنين)", description: "الاجابه الصحيحة :\n1-مناقشة مخاطر تأخير المشروع وأولويات العمل مع المديرين الوظيفيين المعنيين\n2-اعمل مع الفريق لتحديد الخيارات لتسريع تسليم المشروع.", options: [{ option: "اعمل مع الفريق لتحديد الخيارات لتسريع تسليم المشروع.", answer: "true" }, { option: "مناقشة مخاطر تأخير المشروع وأولويات العمل مع المديرين الوظيفيين المعنيين", answer: "true" }, { option: "تنبيه أصحاب المصلحة على الفور بتأخير الجدول الزمني", answer: "false" }, { option: "قم بإنشاء قوائم انتظار يومية لمراجعة تقدم المشروع وتتبع إنجاز المخرجات", answer: "false" }, { option: "السماح لأعضاء الفريق باللحاق بالوتيرة التي تناسبهم", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أنت مدير مشروع تدير مشروعًا متعدد الجنسيات تمت الموافقة على خط الأساس للمشروع. أثناء تنفيذ المشروع ، اكتشف مدير المشروع ظهور العديد من المخاطر. لم يتوقع مدير المشروع هذه المخاطر خلال مرحلة التخطيط للمشروع. ما الذي يجب أن يحسبه مدير المشروع لمعالجة هذا الوضع الحرج للميزانية؟ (إختر إثنين)", description: "الاجابة الصحيحة :\nمراقبة احتياطي الطوارىء في المشروع وإعادة تعيين خط الأساس التحكم المتكامل في التغيير في المشروع", options: [{ option: "سعر صرف العملة", answer: "false" }, { option: "التحكم في التخفيف في المشروع", answer: "false" }, { option: "التحكم المتكامل في التغيير في المشروع", answer: "true" }, { option: "مراقبة إدارة المنافع في المشروع", answer: "false" }, { option: "مراقبة احتياطى الطوارىء في المشروع وإعادة تعيين خط الأساس", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أنت مدير لمشروع معقد ؛واجه مقدم خدمة في المشروع مشاكل تتعلق بتوفر الموارد خلال الأشهر الأربعة الماضية. ومع ذلك ، فقد أكد مقدم الخدمة لمدير المشروع أن النواتج ستكون في الوقت المحدد. بصفتك مدير مشروع، كيف تتعامل مع هذا الموقف؟", description: "الكلمة المفتاحية ( مشاكل توفر الموارد)\nهذه مشكلة ويجب على مدير المشروع تحديث سجل الاشكالات الخيار إجراء تحليل السبب الجذري غير صحيح لأنها مشكلة بائع وليس مدير المشروع او المشروع نفسه. الخيارات الأخرى غير صحيحة لأن هذه مشكلة وليست خطر.", options: [{ option: "تطبيق خطة الاستجابه للخطر", answer: "false" }, { option: "إجراء التحليل النوعى للخطر", answer: "false" }, { option: "إجراء تحليل السبب الجذري", answer: "false" }, { option: "تحديث سجل الاشكالات", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أثناء المشروع ، يدرك مدير المشروع أن صاحب مصلحة جديد مهتم بالمشروع ويريد التأكد من أن أصحاب المصلحة على دراية كاملة بأهداف المشروع وأهدافه ومخاطره. بصفتك مدير مشروع، ما الذي يجب عليك فعله لتعزيز نجاح المشروع؟", description: "الاجابه الصحيحة هي تحديث خطة مشاركة المعنيين\nالكلمة المفتاحية ( يدرك مدير المشروع) مدير المشروع بالفعل صنف صاحب المصلحة بأنه مهتم. لذا فإن الخطوة التالية هي تحديث خطة إشراك أصحاب المصلحة بناءً على احتياجاته وتوقعاته واهتماماته وتأثيره المحتمل على المشروع ليحسن من نجاح المشروع.", options: [{ option: "شارك تقارير حالة المشروع مع صاحب المصلحة الجديد", answer: "false" }, { option: "تحديث خطة مشاركة المعنيين", answer: "true" }, { option: "تحديد موعدًا مع صاحب المصلحة الجديد", answer: "false" }, { option: "أضف صاحب المصلحة الجديد إلى خطة إدارة الاتصالات", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أبلغ البائع مدير المشروع أن أحد الموارد المهمة سيكون في إجازة طويلة. راجع فريق المشروع مخرجات البائع المعلقة وحدد حلّا بديلًا ، لكن الحل سيتكبد تكلفة إضافية. المشروع حاليا في الموعد المحدد وهو أقل قليلا من الميزانية. ماذا يجب أن يفعل مدير المشروع بعد ذلك؟", description: "الإجابة الصحيحة هي: تنفيذ التحكم المتكامل في التغيير لأن الحل سيتكبد تكلفة إضافية.", options: [{ option: "طلب استبدال المورد", answer: "false" }, { option: "أداء التحكم المتكامل في التغيير", answer: "true" }, { option: "اجراء تحليل التكلفة مقابل المنفعه", answer: "false" }, { option: "تنفيذ خطة الاستجابة", answer: "false" }] }, { type: "dragdrop", degree: 0, chapter: "", domain: "", name: "اسحب استراتيجية المخاطر من على اليسار إلى إجراءات مدير المشروع على اليمين", description: "", options: [{ option: "-aقام مدير المشروع بتحليل التهديد . واتخذ إجراءات لتقليل احتمالية حدوثه ، وأجرى بعض الاختبارات الإضافية", answer: "2-القبول" }, { option: "b-حدد مدير المشروع التهديد وقرر مراجعته بشكل دوري بدلاً من اتخاذ إجراء", answer: "3-التجنب" }, { option: "c-قرر مدير المشروع أن التهديد كان شديدًا وتصرف على الفور للتعامل مع هذا الخطر من خلال تغيير استراتيجيات المشروع وتقليل نطاق المشروع.", answer: "4-التصعيد" }, { option: "d-درس مدير المشروع التهديد ووجد أنه خارج نطاق سلطته ونطاق المشروع", answer: "5-التحويل" }, { option: "e-حدد مدير المشروع التهديد , وناقشه مع الراعي , واشترى خطة تأمين للتعامل معه", answer: "1-التخفيف" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "بعد الموافقة على المشروع، يخبر أحد أصحاب المصلحة الرئيسيين مدير المشروع أن استراتيجية إدارة المشروع الحالية غير محددة جيدًا. كما يتم إبلاغ مدير المشروع أيضًا أن صاحب المصلحة الرئيسي لا يريد عقد جلسة عمل، فما الذي يجب أن يفعله مدير المشروع أولاً؟", description: "الاجابه الصحيحة :\nشارك بوثائق المشروع وادعُ أصحاب المصلحة الرئيسيين لمناقشة أي مخاوف", options: [{ option: "اطلب من راعي المشروع ممارسة السلطة على أصحاب المصلحة الرئيسيين من أجل الحفاظ على الجدول الزمني المعتمد", answer: "false" }, { option: "شارك بوثائق المشروع وادعُ أصحاب المصلحة الرئيسيين لمناقشة أي مخاوف", answer: "true" }, { option: "قم بدعوة أصحاب المصلحة الرئيسيين إلى مجلس التحكم في تغيير المشروع لمناقشة تعديل استراتيجية المشروع", answer: "false" }, { option: "تابع جلسة العمل للفرق التي لا تنتمي إلى صاحب المصلحة الرئيسي هذا", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "بعد عدة تكرارات ، يقرر مدير المشروع تقديم المنتج إلى أحد المعنيين الرئيسيين. وكانت أحد هذه الملاحظات هي أن الفريق أخطأ في العديد من الميزات ما الذي يجب أن يفعله مدير المشروع لتجنب ذلك في المستقبل؟", description: "يجب عقد اجتماع مراجعة التكرار بعد نهاية كل تكرار لجمع التعليقات المبكرة من المعنيين وتكييف الخطة وفقًا لذلك.", options: [{ option: "إعداد جلسات جمع المتطلبات مع جميع المعنيين الرئيسيين لتقييم النطاق مرة أخرى وإعادة ترتيب أولويات سجل أعمال المنتج بناءً على الجلسات.", answer: "false" }, { option: "تقييم مهارات الفريق للبحث عن شخص لديه خبرة في تصميم المنتج وتكليف هذا الشخص بمهمة إعادة بناء واجهة المنتج", answer: "false" }, { option: "جدولة اجتماع المراجعة للتكرار لجمع التعليقات المبكرة من المعنيين وتكييف الخطة وفقًا لذلك", answer: "true" }, { option: "دمج ممارسات التفكير التصميمي في دورة حياة المشروع لفهم شخصيات المنتج بشكل أفضل وتكون أكثر فاعلية لمطابقة احتياجاتهم.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "اثناء تطوير منتج جديد ، يعرب أحد المعنيين الرئيسيين عن قلقه بشأن عدم كفاية المعلومات حول متطلبات ميزات المنتج لا يوافق فريق المنتج ويذكر أنه تم توفير المتطلبات التفصيلية.\u000bما الذي يجب أن يفعله مدير المشروع للتعامل مع هذا الموقف؟", description: "تنظيم مناقشة مع فريق المنتج لتوضيح المعلومات المفقودة.", options: [{ option: "تنظيم مناقشة مع فريق المنتج لتوضيح المعلومات المفقودة.", answer: "true" }, { option: "تسجيل المتطلبات الجديدة واطلب من فريق المنتج تطويرها.", answer: "false" }, { option: "إجراء تحليلات جديدة للمخاطر والنطاق، وتحديد المعلومات المفقودة.", answer: "false" }, { option: "أعد توجيه صاحب المصلحة لمناقشة هذا الطلب مع المدير الوظيفي.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أثناء عملية الإغلاق ، يعلم مدير المشروع أن أحد المخرجات لا يلبي توقعات العميل، ماذا يجب ان يفعل مدير المشروع ؟", description: "مراجعة متطلبات التسليم ، والتحقق من معايير موافقة العميل ، والمتابعة وفقًا لذلك.", options: [{ option: "يطلب من العميل إصدار طلب تغيير بالمعلومات لتحليلها وتقديم حل.", answer: "false" }, { option: "التواصل مع راعي المشروع وطلب المساعدة في التفاوض بشأن إغلاق المشروع مع العميل", answer: "false" }, { option: "مراجعة متطلبات التسليم ، والتحقق من معايير موافقة العميل ، والمتابعة وفقًا لذلك", answer: "true" }, { option: "الاجتماع مع العميل للتوصل إلى اتفاق حول نطاق التسليم الذي يرضي الطرفين", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يتم تعيين مدير لمشروع (ERP ) يؤكد راعى الاعمال على أهمية إظهار القيمة على طول المشروع، مع الحفاظ على سلامة المنتج النهائي. كيف يجب على مدير المشروع إنشاء نموذج التسليم لهذا المشروع؟  ", description: "تنظيم المشروع الى مجموعات فرعية متعددة بناءً على وظائف العمل وتسليم كل مجموعة فرعية كمكون للمنتج النهائي", options: [{ option: "إعادة تأسيس المشروع كبرنامج له عدة مشاريع صغيرة، حيث سيتم تسليم كل مشروع كمنتج خاص به.", answer: "false" }, { option: "إبلاغ آخر حالة إلى اللجنة التوجيهية وتقديم عرض توضيحي للميزات المكتملة", answer: "false" }, { option: "تنظيم المشروع الى مجموعات فرعية متعددة بناءً على وظائف العمل وتسليم كل مجموعة فرعية كمكون للمنتج النهائي", answer: "true" }, { option: "جدولة العروض التوضيحية الروتينية على الميزات المكتملة وتقديم منتج واحد بمجرد اكتمال جميع الميزات.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "مدير مشروع يدير مشروع هجين . أثناء تنفيذ المشروع، تم إبلاغ مدير المشروع أنه تمت إعادة تخصيص أكثر من نصف ميزانية المشروع لمشروع يتعامل مع حالة طوارئ حدثت مؤخرًا. ماذا يجب أن يفعل مدير المشروع؟", description: "مقابلة مالك المنتج لتأكيد الخيار الأدنى للمنتج القابل للتطبيق(MVP).\nالخيار لقاء مع فريق المشروع وإعادة ترتيب سجل اعمال المنتج غير صحيح لأن هذه مسؤولية مالك المنتج.الخيار اللقاء مع راعي المشروع لشرح أنه لا يمكن إكمال المشروع كما هو مخطط له. غير صحيح لأن مالك المنتج يمكنه تأكيد خيار الحد الأدنى من المنتج القابل للتطبيق(MVP).\nوالحد الأدنى من المنتج القابل للتطبيق MVP هو إصدار من منتج يحتوي على ميزات كافية فقط ليتم استخدامها من قبل العملاء الذين يمكنهم بعد ذلك تقديم ملاحظات للمستقبل.\n مع MVP ، جميع أصحاب المصلحة لديهم فرصة لرؤية وتجربة شكل من أشكال نتائج المشروع.", options: [{ option: "مقابلة القائد الفني لمراجعة خطة التكرار.", answer: "false" }, { option: "الاجتماع مع فريق المشروع وعادة ترتيب أولويات سجل اعمل المنتج.", answer: "false" }, { option: "الاجتماع راعي المشروع لتوضيح أن المشروع لا يمكن أن يكتمل كما هو مخطط له", answer: "false" }, { option: "قابل مالك المنتج لتأكيد الخيار الأدنى للمنتج القابل للتطبيق(MVP).", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يريد مدير المشروع التأكد من استيفاء معايير قبول المشروع لجميع مخرجات المشروع ما الذي يجب أن يفعله مدير المشروع لتحقيق ذلك؟", description: "تضمين توقعات واحتياجات المعنيين في المشروع أثناء إنشاء معايير قبول المشروع", options: [{ option: "جدولة اجتماعات منتظمة مع المعنيين في المشروع للتأكد من رضاهم", answer: "false" }, { option: "تضمين توقعات واحتياجات المعنيين في المشروع أثناء إنشاء معايير قبول المشروع", answer: "true" }, { option: "مناقشة معايير قبول المشروع مع راعي المشروع قبل مرحلة التنفيذ", answer: "false" }, { option: "إنشاء بيان عمل (SOW) وتأكد من بقائه بدون تغيير خلال دورة حياة المشروع", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أثناء اجتماع حالة المشروع مع كبار المديرين ، يقدم مدير المشروع تحديثًا حول حالة التسليمات. يدعي بعض كبار المديرين أنهم لا يعرفون شيئًا عن تسليمات المشروع. ماذا يجب أن يفعل مدير المشروع بعد ذلك؟", description: "الإجابة الصحيحة هي: الاجتماع مع المعنيين لمراجعة ميثاق المشروع. يتضمن ميثاق المشروع وصفًا عالي المستوى للمشروع والتسليمات الرئيسية.", options: [{ option: "تحقق من صحة ما إذا تم تغيير ميثاق المشروع عن الميثاق الأصلي", answer: "false" }, { option: "مراجعة ميثاق المشروع للتحقق من صحة هذه المطالبة", answer: "false" }, { option: "تحقق مما إذا كان المعنيين يحضرون اجتماعات المشروع العادية", answer: "false" }, { option: "الاجتماع بالمعنيين لمراجعة ميثاق المشروع", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "انتقل مدير المشروع مؤخرًا إلى مؤسسة لقيادة مشروع رئيسي. يلاحظ مدير المشروع أن أداء الفريق أقل من الهدف، ولا يتم تسليم المهام المعينة في الوقت المحدد. كما أن التزام الفريق ليس مناسبًا كما ينبغي. ماذا يجب أن يفعل مدير المشروع؟", description: "دراسة وتحديد أسلوب القيادة المناسب المناسب للفريق.", options: [{ option: "استعن بقائد فريق المشروع الذي يمكنه التأثير على أعضاء فريق المشروع", answer: "false" }, { option: "تطبيق نهج رشيق للمساعدة في تحسين أداء الفريق", answer: "false" }, { option: "حدد موعدًا للاجتماع وقم بإبلاغ الفريق عن مخاوف الأداء", answer: "false" }, { option: "دراسة وتحديد أسلوب القيادة المناسب المناسب للفريق", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "مدير المشروع يقود مشروع هجين تم تطوير هيكل تجزئة العمل والجدول الزمني، ولكن يواجه مدير المشروع صعوبة في تخصيص موارد محددة لجميع مهام المشروع. ماذا يجب أن يفعل مدير المشروع؟", description: "قم بتعيين المهام غير المخصصة إلى الدور الوظيفي ثم إعادة تخصيصها عند توفر الموارد يصف الدور الوظيفي مجموعة من المهارات والأنشطة التي يؤديها عادةً قسم داخل مؤسسة، على سبيل المثال ، مدير أو مدير أو نائب رئيس. يتم تحديد الأدوار الوظيفية من خلال الهيكل التنظيمي للشركة. يتم تعيين الموارد لهذه الأدوار داخل هذا القسم.", options: [{ option: "تجاهل المهام غير المخصصة في الوقت الحالي وأعد تخصيصها عند توفر الموارد", answer: "false" }, { option: "قم بتعيين المهام غير المخصصة إلى مدير مكتب إدارة المشروع وأعد تخصيصها عند توفر الموارد", answer: "false" }, { option: "تصعيد المشكلة إلى راعي المشروع والسعي لتدخله الفوري", answer: "false" }, { option: "قم بتعيين المهام غير المخصصة إلى الدور الوظيفي ثم إعادة تخصيصها عند توفر الموارد", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "نظرا لظروف غير متوقعة ، لم يتمكن المقاول من تعبئة فريق المشروع، وهو الأمر الذي كان ضروريًا لإجراء اجتماعات ومناقشات وجهًا لوجه. تم عقد اجتماعات افتراضية لاحقا ، لكن فريق المشروع المحلي والمستخدمين النهائيين يواجهون الآن فجوة في الاتصال تتسبب في تدني أداء الفريق. ماذا يجب أن يفعل مدير المشروع؟", description: "أعد التقييم باستمرار لإزالة الحواجز عن الفريق", options: [{ option: "اطلب من المقاول حل هذه المشكلة لأن هذا التزام تعاقدي", answer: "false" }, { option: "تنفيذ مؤشرات الأداء الرئيسية (KPIs) وإقناع المقاول بإشراك الفريق", answer: "false" }, { option: "أعد التقييم باستمرار لإزالة الحواجز عن الفريق", answer: "true" }, { option: "إنهاء العقد وبدء عقد جديد مع مقاول محلي", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "منظمة تتبنى عقلية رشيقة. في أول مشروع رشيق ، واجه مدير المشروع مشكلة لأن الفريق لم يكن قادرًا على اتخاذ القرارات في الوقت المحدد.\u000bما الذي يجب أن يفعله مدير المشروع لمعالجة المشكلة؟", description: "قم بتقييم الموقف لفهم كيف تؤثر ثقافة المنظمة على عملية صنع القرار، وقم بتدريب الفريق نحو نموذج جديد.", options: [{ option: "اعتماد أسلوب قيادة استبدادي وإملاء جميع القرارات من أجل تسريع سرعة الفريق.", answer: "false" }, { option: "تحديد مبادئ توجيهية واضحة حول كيفية اتخاذ القرارات في إطار السياسة التنظيمية الجديدة وتعزيزها مع الفريق في كل مناسبة.", answer: "false" }, { option: "قم بتقييم الموقف لفهم كيف تؤثر ثقافة المنظمة على عملية صنع القرار ، وقم بتدريب الفريق نحو نموذج جديد.", answer: "true" }, { option: "ادعُ القيادة العليا إلى ورشة عمل لبناء الفريق وأعد التأكيد على أهمية المساءلة والقيادة في ظل التوجيه الجديد للشركة.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "تم تعيين مدير مشروع صغير مؤخرًا لقيادة مشروع معقد من المقرر أن يبدأ في غضون أسبوعين تم تقديم مدير المشروع المبتدئ إلى مدير مشروع آخر للتوجيه. ما هي الفائدتان اللتان سيحققهما مدير المشروع المبتدئ من خلال تكليفه بموجه؟ (إختر إثنين)", description: "1- يمكن للموجه تقديم وثائق المشروع السابقة مع مؤشرات الإنذار المبكر التي قد تحدث مشاكل محتملة\n2- يمكن لمدير المشروع المبتدئ مناقشة بعض المشكلات مع معلمه دون خوف من العقاب", options: [{ option: "يمكن لمدير المشروع المبتدئ مناقشة بعض المشكلات مع معلمه دون خوف من العقاب", answer: "true" }, { option: "يمكن للموجه تقديم وثائق المشروع السابقة مع مؤشرات الإنذار المبكر التي قد تحدث مشاكل محتملة", answer: "true" }, { option: "سيكون المرشد متاحًا على الفور لتولي المشروع إذا كانت هناك مشاكل أو تأخيرات في المشروع", answer: "false" }, { option: "سيشارك المرشد في عمليات صنع القرار التي ينفذها مدير المشروع المبتدئ", answer: "false" }, { option: "يقدم مدير المشروع المبتدئ تقاريره إدارياً إلى معلمه بحيث يمكن الإجابة على أسئلة الموارد البشريةHR بسرعة.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "في اجتماع اللجنة التوجيهية للمشروع ، أثار عدد قليل من المعنيين مخاوف من أن تقرير مدير المشروع يوضح أن نتيجة المشروع موجودة بالفعل في السوق بينما لا يزال المشروع قيد التطوير. يوضح مدير المشروع أن المشروع يتم تسليمه على تكرارات ويتضمن إصدارات مختلفة. ما الذي يجب على مدير المشروع فعله لتجنب حدوث هذا السيناريو؟", description: "يتأكد أن محتوى تقرير المشروع يكون مفهوم بوضوح ويطلب أي ملاحظات ضرورية", options: [{ option: "يقوم بدعوة المعنيين المهمين إلى اجتماعات مراجعة المشروع اليومية لضمان مشاركتهم الكاملة ومشاركتهم", answer: "false" }, { option: "يتأكد أن محتوى تقرير المشروع يكون مفهوم بوضوح ويطلب أي ملاحظات ضرورية", answer: "true" }, { option: "تأكد من اكتمال جميع ميزات المنتج وفقًا للمواصفات المبلغ عنها قبل طرحها في السوق", answer: "false" }, { option: "إرسال تقرير مفصل إلى كل عضو من أعضاء اللجنة التوجيهية قبل الاجتماع", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "خلال اجتماع الفريق ، يدرك مدير المشروع الجديد أن هناك العديد من الشكاوى من العميل فيما يتعلق باستقرار المشروع والقضايا الحرجة الأخرى ذكر أحد أعضاء الفريق أن المخرجات تم تطويرها من قبل موظفين ذوي خبرة أكدوا أن المشروع مستقرا كان كيف ينبغي لمدير المشروع الجديد معالجة هذه المخاوف؟", description: "يقوم بتوثيق شكاوى العميل والمشكلات الحرجة في سجل المشكلات", options: [{ option: "يعقد جلسة عصف ذهني لتحديد السبب الجذري للشكاوى", answer: "false" }, { option: "يطلب من الفريق الحصول على ملاحظات إضافية وأضف المهام إلى لوحة المهام", answer: "false" }, { option: "يقوم بتوثيق شكاوى العميل والمشكلات الحرجة في سجل المشكلات", answer: "true" }, { option: "يسمح لفريق أجايل بتحديد ما يمكنهم تقديمه في كل تكرار", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "مشروع أجايل في التكرار الثانى من اصل ستة تكرارات. انضم عضو جديد في الفريق ليس لديه خبرة عن الادارة الرشيقة. في الاجتماعات اليومية، يلاحظ مدير المشروع أن الفريق\u000bيواجه مشاكل في تحقيق أهدافه. ماذا يجب أن يفعل مدير المشروع؟", description: "لم يذكر في السؤال ان المشكلة بسبب انضمام عضو الفريق الجديد ، ولذلك يجب عليك كمدير مشروع معرفة السبب الجذري لعدم تكرار هذا المشكلة في التكرارات القادمة. ولذلك الاجابه الصحيحة هي تعطى فيدباك للفريق وتعقد اجتماع الدروس المستفادة للعثور على السبب الجذري", options: [{ option: "تعطى فيدباك للفريق وتعقد اجتماع الدروس المستفادة للعثور على السبب الجذري", answer: "true" }, { option: "تعطى فيدباك إلى الفريق وعقد اجتماعًا لمراجعة قواعد الفريق.", answer: "true" }, { option: "تعطى فيدباك للفريق واطلب تدريبًا رشيقا للعضو الجديد.", answer: "false" }, { option: "تعطى فيدباك للفريق وتدرب عضو الفريق الجديد على الطرق الرشيقة.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "حدد أحد اعضاء فريق المشروع عيبا فى تصميم الحل الاولى للمشروع . إن المعنيين غير متاكدين مما اذا كان ينبغى نشر النظام او لا كمدير مشروع ماذا يجب ان تفعل ؟", description: "اعتماد استراتيجية تخفيف المخاطر استنادا الى تصميم الحل", options: [{ option: "اقتراح إعادة تصميم الحل للمشروع", answer: "false" }, { option: "مراجعة قياسات ضبط جودة المشروع", answer: "false" }, { option: "قم بإجراء تحليل للمخاطر بشأن هذه المشكلة.", answer: "false" }, { option: "اعتماد استراتيجية تخفيف المخاطر استنادا الى تصميم الحل", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يواجه مدير المشروع تأخيرات فى مهمتين بالغتى الاهمية يفترض تنفيذهما بواسطة فريقين مختلفين . ويدرك مدير المشروع ان المديرين الوظيفيين لهذين الفريقين هما فى نزاع لم يحل ولا يتواصلان كما يجب، ماذا يجب ان تفعل كمدير مشروع ؟", description: "ترتيب اجتماع مع المديرين لمحاولة التواصل الى اتفاق", options: [{ option: "الاجتماع مع الفريقين ومديريهما ومحاولة التعرف على السبب الجذري للنزاع", answer: "false" }, { option: "ترتيب اجتماع مع المديرين لمحاولة التواصل الى اتفاق", answer: "true" }, { option: "تقييم تأثير التأخير في المشروع والبحث عن بدائل لوضع خطة اصلاحية", answer: "false" }, { option: "تبليغ راعى المشروع بالحاجة الى حل النزاع بين المديرين", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "تم تعيين مدير مشروع جديد لتولى مشروع معرض لخطر الفشل . لا يعلم المعنيون شيئا عن حالة المشروع او المنافع التى سيقدمها . كيف يمكن لمدير المشروع نقل هذه المنافع الى المعنيين ؟", description: "إجراء تحليل التكلفة مقابل المنفعة الخاص بالمشروع للمعنيين\nلذا يجب على مدير المشروع إجراء تحليل التكلفة مقابل المنفعة على مدار المشروع لمساعدة اللجنة المسؤله عن المشروع على اتخاذ القرارات الصحيحة ولضمان تسليم الفوائد وفقًا لخطة إدارة الفوائد. الخيار إنشاء خطة إدارة المنافع وفقا للأهداف الإستراتيجية للمؤسسة) غير صحيح لأنه يجب تطوير هذه الخطة قبل المشروع.", options: [{ option: "إعادة تحديد الخط المرجعى للجدول الزمنى للمشروع لاظهار حالة المشروع الحالية", answer: "false" }, { option: "إنشاء خطة إدارة المنافع وفقا للاهداف الاستراتيجية للمؤسسة", answer: "false" }, { option: "تحديث خريطة التسليمات بالمقارنة مع متطلبات المشروع المتفق عليها", answer: "false" }, { option: "إجراء تحليل التكلفة مقابل المنفعة الخاص بالمشروع للمعنيين", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يقوم مدير المشروع بإدارة مشروع هجين. ولقد تواصل معنى مباشرة مع عضو فى الفريق وطلب تغيير تشتت عضو الفريق عن عمله بسبب الطلب مما تسبب فى انخفاض الانتاجية . ما الذي ينبغى على عضو الفريق فعله؟", description: "ابلاغ المعنى بأن الاجراء الصحيح للمتطلبات الجديدة هو تقديمها مباشرة الى مدير المشروع", options: [{ option: "ابلاغ المعنى بأن الاجراء الصحيح للمتطلبات الجديدة هو تقديمها مباشرة الى مدير المشروع", answer: "true" }, { option: "ابلاغ مدرب اسكرم بشأن طلب المعنى بإجراء تغيير وتأثير ذلك في الانتاجية", answer: "false" }, { option: "ابلاغ المعنى بأن الاجراء الصحيح للمتطلبات الجديدة هو تقديمها مباشرة الى لجنة التحكم في التغيير CCB", answer: "false" }, { option: "ابلاغ مدرب اسكرم بحيث يمكن مناقشة التغيير مع المعنى مباشرة", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "مدير المشروع يدير مشروعا لتطوير أداة جديدة . ويقرر مدير المشروع استخدام الاسلوب الرشيق اجايل بسبب طبيعة المشروع . تصبح إحدى المخاطر المحددة مشكلة عندما يكتشف مدير المشروع أن شركة أخرى حصلت على براءة اختراع لأداة مشابهة جدًا. ماذا يفعل مدير المشروع لمعالجة هذه المشكلة؟", description: "جدولة اجتماع مع المعنيين المهمين لاستعراض المشكلة واتخاذ قرار بشأن الخطوات التالية", options: [{ option: "ابلاغ مالك المنتج وتسجيل المشكلة فى سجل المخاطر لمتابعتها من اجل تحديد الخطوات التالية", answer: "false" }, { option: "جدولة اجتماع مع المعنيين المهمين لاستعراض المشكلة واتخاذ قرار بشأن الخطوات التالية", answer: "true" }, { option: "التواصل مع الشركة التى تمتلك براءة اختراع الاداة المشابهة لمقارنة أوجة التشابه والاختلاف مع المشروع الحالي", answer: "false" }, { option: "تعليق المشروع واعفاء الموارد من المهام الى حين حل المشكلة", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يجرى اعضاء الفريق مناقشة مع مدير المشروع . وفى اجتماع التقييم بأثر رجعى الاخير ادرك الفريق ان المعدات القديمة قد تؤثر في التكرار التالى للمشروع ويوصى الفريق بشراء معدات جديدة لانها امر اساسي لنجاح التسليمات النهائية ما الخطوة التالية التى ينبغى على مدير المشروع اتخاذها.", description: "التحقق من موازنة المشروع للتأكد من وجود احتیاطی طوارىء كاف\n( اختيار تقديم طلب تغيير ) ليس صحيح لان لا يوجد طلبات تغيير في مشاريع اجايل", options: [{ option: "مراجعة خطة إدارة التكلفة لتحديد كيفية التعامل مع هذه المشكلة", answer: "false" }, { option: "التحقق من موازنة المشروع للتأكد من وجود احتیاطی طوارىء كاف", answer: "true" }, { option: "تقديم طلب تغيير لزيادة الموازنة وشراء معدات جديدة", answer: "false" }, { option: "إخبار اعضاء الفريق بأن توفير معدات اضافية ليس جزءا من نطاق المشروع", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يعمل مدير المشروع في منتصف مشروع رشيق يتألف من 100 قصة مستخدم مقسمة إلى خمسة تكرارات، وتبلغ قيمة كل قصة مستخدم 50 دولارًا أمريكيًا. تبلغ التكلفة الفعلية 2000 دولار أمريكي وتم تسليم 50 قصة مستخدم.\u000bما هي حالة المشروع ؟", description: "100 قصة مستخدم مقسمة إلى خمسة تكرارات ( اذا سوف يتم تنفيذ 20 قصة مستخدم في التكرار الواحد)\nالتكلفة الفعلية = 2000\nالقيمة المكتسبة =50*50= 2500\nمعدل أداء التكلفة= 2000/2500= 25، اذا المشروع اقل الميزانية\nالمشروع في منتصف المشروع لذلك القيمة المخططة= 50 قصة مستخدم \nالقيمة المخططة= 50*50= 2500\nمعدل اداء الجدول الزمنى= 2500/2500= 1 لذا فإن المشروع مع الجدول الزمنى", options: [{ option: "المشروع اقل من الميزانية ومتاخر عن الجدول الزمنى", answer: "false" }, { option: "المشروع متجاوز الميزانية ومتاخر عن الجدول الزمنى", answer: "false" }, { option: "المشروع متوافق مع الميزانية ومع الجدول الزمنى", answer: "false" }, { option: "المشروع اقل من الميزانية ومع الجدول الزمنى", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "في منتصف المشروع، تتم ترقية مدير وحدة الأعمال الذي يتفاعل بانتظام مع فريق مدير المشروع إلى منصب إداري أعلى. المدير البديل من نفس الوحدة وهو على دراية بالمشروع إلى حد ما. بعد الحصول على مزيد من المعلومات حول المدير الجديد ، ما هي الخطوة التالية لمدير المشروع؟", description: "تحديث سجل المعنيين وخطة إشراك المعنيين لتشمل المدير الجديد.", options: [{ option: "حدد موعدًا لعقد اجتماع مع المدير الجديد لمناقشة خطط فريق تحليل الأعمال.", answer: "false" }, { option: "أرسل إلى المدير الجديد نسخة من ميثاق المشروع لضمان فهم أهداف المشروع.", answer: "false" }, { option: "اتصل بمدير وحدة الأعمال السابق لمعرفة المزيد عن المدير الجديد.", answer: "false" }, { option: "تحديث سجل المعنيين وخطة إشراك المعنيين لتشمل المدير الجديد.", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "في المشروع العالمي ، يتعين على مدير المشروع تخطيط وإدارة الاتصال بين أعضاء الفريق والمعنيين من أجل ضمان بقاء المشروع على المسار الصحيح وتحقيق أهدافه.\u000bماذا يجب أن يفعل مدير المشروع؟", description: "الاخذ في الاعتبار الاختلافات الثقافية بين المعنيين لضمان بقاء المشروع على المسار الصحيح وتحقيق أهدافه.", options: [{ option: "فهم كيف يمكن للجوانب السياسية أن تؤثر على المشروع.", answer: "false" }, { option: "حدد موعدًا للتدريب على الاتصال للمعنيين على مستوى الإدارة.", answer: "false" }, { option: "عقد اجتماع تكامل بين جميع المعنيين.", answer: "false" }, { option: "الاخذ في الاعتبار الاختلافات الثقافية بين المعنيين.", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "في مشروع رشيق ، يجد مدير المشروع أن أعضاء الفريق يشاركون في عدة مشاريع مختلفة بسبب قيود الميزانية. كمدير مشروع ، ماذا يجب أن تفعل ؟", description: "يجب أن يعمل فريق أجايل على مشروع واحد. لذلك يجب على مدير المشروع أن يطلب من راعي\nالمشروع تحديد الأولويات ، وإذا لزم الأمر، تخصيص ميزانية إضافية حتى يتمكن الفريق من العمل في مشروع واحد سؤال مهم جدا للامتحان الحقيقي. ", options: [{ option: "تقديم خطة حوافز حتى يعمل أعضاء الفريق لساعات غير محسوبة أكثر في مشاريع متعددة", answer: "false" }, { option: "يطلب من أعضاء الفريق أن يتوزعوا بالتساوي عبر المشاريع حتى لا تحدث أخطاء ضمن الميزانية الحالية", answer: "false" }, { option: "اطلب من راعي المشروع تحديد الأولويات وبتخصيص ميزانية إضافية حتى يتمكن الفريق من العمل في مشروع واحد", answer: "true" }, { option: "تبادل الأفكار مع أعضاء الفريق حول كيفية تنفيذ مشاريع متعددة ضمن الميزانية المحددة", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "ما الإجراءان اللذان يجب على مدير المشروع مراعاتهما أثناء تعيين فريق افتراضي جديد؟ اختر اجابتين", description: "الاجابه الصحيحه:\nحدد في البداية كيف سيتم رصد التقدم وأفضل الوسائل لإبلاغ التقدم\nالتحقق مما إذا كان أعضاء الفريق في أكثر من فريق ولا يمكن تخصيص الوقت الكافي لهذا الفريق\n\nسؤال مهم جدا للاختبار الحقيقي", options: [{ option: "تحقق مما إذا كان أعضاء الفريق في أكثر من فريق ولا يمكن تخصيص الوقت الكافي لهذا الفريق", answer: "true" }, { option: "يقوم يتعيين أفضل الموارد داخل المنظمة لتكون جزءًا من الفريق الافتراضي", answer: "false" }, { option: "حدد في البداية كيف سيتم رصد التقدم وأفضل الوسائل لإبلاغ التقدم", answer: "true" }, { option: "تحديد أنواع وكميات الموارد المطلوبة لكل حزمة عمل أو نشاط", answer: "false" }, { option: "يقوم بتقييم نقاط القوة والضعف لدى مدير المشروع كقائد افتراضي للفريق لتحديد العوائق التي يمكن تجنبها", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يحتاج المعنيين في مشروع رشيق إلى توضيح مستمر لقيمة أعمال المشروع. كيف يجب على مدير المشروع المضي قدما؟ ", description: "يقوم بتضمين العروض التوضيحية للجزء المبني من المنتج في نهاية كل تكرار.   سؤال مهم جدا للاختبار الحقيقي", options: [{ option: "إعداد تقارير قيمة الأداء للأعمال بشكل مستمر.", answer: "false" }, { option: "يقوم بتأكيد الحد الأدنى من المنتج القابل للتطبيق واعرضه على العميل عندما يكون جاهزًا.", answer: "true" }, { option: "يقوم بتضمين العروض التوضيحية للجزء المبني من المنتج في نهاية كل تكرار.", answer: "false" }, { option: "إعداد تقرير المشروع للمعنيين وتوزيعه أسبوعيا.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أثناء تنفيذ المشروع ، يحتاج أحد أعضاء فريق المشروع الرئيسيين إلى أخذ إجازة غير متوقعة. المشروع بحالة جيدة سابق الجدول الزمني واقل من الميزانية المخططة . ما الإجراءان اللذان يجب على مدير المشروع اتخاذه في البداية لحل هذه المشكلة؟ (إختر إثنين)", description: "الاجابه الصحيحة:\u000b*التحقق من سجل المخاطر للحصول على استجابة في حالة غياب مورد بشكل مفاجيء.\u000b*التحقق من مصفوفة تعيين المسؤليات للمشروع لتحديد الأنشطة التي ستتأثر\u000bسؤال مهم جدا للاختبار الحقيقي.", options: [{ option: "تحقق من مصفوفة تعيين المسؤليات للمشروع لتحديد الأنشطة التي ستتأثر.", answer: "true" }, { option: "التحقق من سجل المخاطر للحصول على استجابة فى حالة غياب مورد بشكل مفاجيء.", answer: "true" }, { option: "الحصول على دعم راعى المشروع ليحل محل عضو الفريق في إجازة.", answer: "false" }, { option: "إعادة تعيين المسؤوليات عبر الأعضاء المتبقين باستخدام السماحية المتاحة للتدريب.", answer: "false" }, { option: "تحقق مما إذا كانت أنشطة أعضاء الفريق موثقة بشكل كافٍ لتسهيل التسليم.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "إذا أراد مدير المشروع التأكد من أن العمل المنجز والمنتج الذي يتم إنشاؤه لا ينحرف عن قيمة العمل المحددة. ما الذي يجب أن يوليه مدير المشروع اهتمامًا خاصًا لتحقيق هذا الهدف؟", description: "يجب أن يولي مدير المشروع اهتماما خاصا لمراقبة خطة تحقيق المنافع وتحديثها للتأكد من أن العمل المنجز والمنتج الذي يتم إنشاؤه .لا ينحرف عن قيمة الأعمال المحددة\n*خطة تحقيق المنافع : وثيقة تحدد الأنشطة الضرورية لتحقيق المنافع المخططة. يحدد الجدول الزمني والأدوات والموارد اللازمة لضمان تحقيق الفوائد بالكامل بمرور الوقت. يحدد الفوائد والافتراضات المرتبطة بها ، وكيف سيتم تحقيق كل فائدة\n*سؤال مهم للاختبار الدولي", options: [{ option: "مراقبة هيكل تجزئة العمل (((WBS)", answer: "false" }, { option: "مراجعة وتحديث خطة إدارة المتطلبات", answer: "false" }, { option: "إجراء عمليات تدقيق الجودة وتحليل الفشل", answer: "false" }, { option: "مراقبة وتحديث خطة تحقيق المنافع", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يستخدم مدير المشروع نهجًا مختلطا لتسليم المشروع. تم تحديد حجم فريق التطوير، ولكن لم يتم تعيين كامل فريق التطوير بعد. يعمل مدير المشروع على تحديد الجدول الزمني للمشروع.\u000bما الذي يجب على مدير المشروع فعله لمعالجة مسألة توفر المطور؟", description: "الجواب الصحيح هو:\nتنظیم جلسة تخطيط مع فريق التطوير واستخدم تقديراتهم لإكمال الجدول.\nيجب تقدير الأنشطة من قبل الفريق الذي يعمل على المهمة حسب سرعته لتكون أكثر دقة وواقعيه.\n*سؤال مهم جدا", options: [{ option: "اطلب من الراعي تقديم تاريخ الانتهاء المطلوب لأنشطة المشروع وإكمال الجدول الزمني.", answer: "false" }, { option: "تنظيم جلسة تخطيط مع فريق التطوير واستخدم تقديراتهم لإكمال الجدول.", answer: "true" }, { option: "استخدم حكم الخبراء لتقدير جميع المهام وإكمال الجدول الزمني في الوقت المخصص.", answer: "false" }, { option: "اسأل مدير الموارد عندما يكون المقاول الجديد متاحًا للبدء وتغيير تاريخ التسليم لجدول المشروع.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "في مراجعة التكرار لمشروع اجايل، يتلقى الفريق تعليقات من أصحاب المصلحة يمكن أن تؤدي إلى تغيير في المنتج. ما الإجراءان اللذان يجب على مدير المشروع اتخاذه؟ )إختر إثنين)", description: "الاجابه الصحيحة  :\nيسأل أصحاب المصلحة عن أولوية هذا التغيير.\nيطلب من مالك المنتج مراجعة سجل أعمال المنتج. \n*سؤال مهم جدا للاختبار الحقيقي", options: [{ option: "يطلب من أعضاء الفريق تحليل تأثير هذا التغيير", answer: "false" }, { option: "يطلب من الراعي الموافقة على طلب التغيير", answer: "false" }, { option: "يطلب من مالك المنتج مراجعة سجل أعمال المنتج", answer: "true" }, { option: "يسأل أصحاب المصلحة عن أولوية هذا التغيير", answer: "true" }, { option: "يطلب من أعضاء الفريق تضمين التغيير في التكرار التالي", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "طلب من مدير المشروع تقديم مؤشر إلى الوقت المتبقي في مشروع اجايل. ما هي الأدوات التي يمكن استخدامها لتوفير هذه المعلومات؟", description: "مخطط الاحتراق هو أداة بسيطة تتعقب تقدم عمل الفريق مقابل مقدار الوقت المتبقي لإكمال العمل يوضح الرسم البياني المعدل المثالي للجهد اللازم للوصول إلى اكتمال العمل في تاريخ محدد. يساعدك استخدام مخطط الإنهيار على : \nتواصل التقدم\nتتبع العمل المتبقى\nالتخطيط للأحداث المستقبلية\nالوصول إلى إنجاز العمل في الوقت المحدد\n\n*سؤال مهم للاختبار الحقيقي", options: [{ option: "اصدار مخطط الاحتراق burndown chart", answer: "true" }, { option: "مخطط باريتو", answer: "false" }, { option: "تحليل وثيقة متطلبات العمل", answer: "false" }, { option: "نقاط القصة لقصص المستخدم المتبقية", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يقابل أحد محللى الاعمال في المشروع العميل فى المصعد ويخبره بأن تطوير المنتج سيستغرق وقتا اطول من المتوقع بسبب التغييرات المستمرة في المتطلبات . ماذا تفعل كمدير مشروع؟", description: "تقديم طلب تغيير الى لجنة التحكم فى التغيير ليعكس المتطلبات الجديدة والجدول الزمنى الاطول للمشروع", options: [{ option: "الرجوع الى خطة إدارة الموارد لمعرفى ما ادا كان من الممكن تعيين مزيد من محللى الاعمال لمعالجة التغييرات في المشروع", answer: "false" }, { option: "لفت انتباه فريق المشروع والعميل الى ان مدير المشروع هو الوحيد المخول بالاعلان عن كل المعلومات المتعلقة بالجدول الزمنى للمشروع", answer: "false" }, { option: "توضيح ضرورة توزيع الاخبار المهمة عن طريق استخدام طرق تواصل تتبعها فقط", answer: "false" }, { option: "تقديم طلب تغيير الى لجنة التحكم فى التغيير ليعكس المتطلبات الجديدة والجدول الزمنى الاطول للمشروع", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "خلال كل مرحلة من مراحل المشروع الجاري، يختلف أحد أعضاء الفريق الهندسي التابع للعميل باستمرار مع مدير المشروع. عضو الفريق ليس لديه سلطة في المشروع ولكن يتم استشارته من قبل الإدارة العليا حول تقدم\u000bالمشروع. ما الذي يجب أن يفعله مدير المشروع أولاً؟", description: "مراجعة سجل المعنيين لتحديد ما اذا كان عضو الفريق متضمن فيه", options: [{ option: "مراجعة سجل المعنيين لتحديد ما اذا كان عضو الفريق متضمن فيه", answer: "true" }, { option: "إصدار طلب تغيير لتوضيح النطاق", answer: "false" }, { option: "تصعيد هذا الاشكال الى الراعى", answer: "false" }, { option: "استخدام المهارات الشخصية لمقابلة عضو الفريق على انفراد للتوافق بين التوقعات", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "وافقت لجنة التحكم في التغيير للتو على طلب تغيير لتعديل أحد التسليمات الرئيسية في أي عملية سيتم تنفيذ هذا التغيير؟", description: "سيتم تنفيذ طلب تغيير معتمد للإجراءات التصحيحية والإجراءات الوقائية وإصلاحات العيوب في عملية توجيه وإدارة عمل المشروع", options: [{ option: "المراقبة والتحكم في أعمال المشروع.", answer: "false" }, { option: "إجراء التحكم المتكامل في التغيير.", answer: "false" }, { option: "إغلاق المشروع أو المرحلة.", answer: "false" }, { option: "توجيه وإدارة أعمال المشروع.", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "اقترب موعد الانتهاء من حدث هام ، لكن الفريق المشارك من قسم اخر غير قادر على الالتزام بتحقيقه في الموعد المحدد . ما الذي يجب أن يفعله مدير\u000bالمشروع للوفاء بالتاريخ المحدد؟", description: "الكلمة الأساسية ( فريق مشارك) مممما يعنى ان مدير المشروع يعمل في مصفوفة ضعيفة أو منظمة وظيفية. لذلك يجب على مدير المشروع التواصل مع المدير الوظيفي للفريق المشارك لطلب دعم إضافي. الخيار (تذكير الفريق المشترك بأهمية المعلم ليس إجراءً جيدًا لأنهم بالفعل غير قادرين على الالتزام بتحقيقه في الموعد المحدد الخيار طلب موارد إضافية) غير صحيح لأن مدير المشروع ليس لديه سلطة ويجب أن يطلب الدعم من المدير الوظيفي.\n الخيار(اطلب من العميل تغيير تاريخ الحدث) ليس ممارسة جيدة في مثل هذه الحالة.", options: [{ option: "اطلب من المدير الوظيفي للفريق المشارك دعم إضافي.", answer: "true" }, { option: "اطلب من العميل تغيير تاريخ الحدث الهام .", answer: "false" }, { option: "اطلب موارد إضافية.", answer: "false" }, { option: "ذكر الفريق المشترك بأهمية الحدث.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أحد الافراء الذي يعمل فى أحد أقسام مؤسستك وجد طريقة رائعة لتضمين عدد قليل من الخبراء المتخصصين في المشروع على الرغم من أن هؤلاء الخبراء سيعملون من منطقة جغرافية أخرى. تم تعيينك كمدير للمشروع في مرحلة التنفيذ. لاحظت بعض التعارضات في فريق المشروع بسبب هذا\u000bالترتيب للأشخاص الذين يعملون من مواقع بعيدة. ما الذي ستفعله أولاً لتحسين الوضع؟", description: "تخطيط الاتصالات مهم جدًا في بيئة فريق افتراضية مثل هذه سيكون من المفيد وجود موارد جغرافية أخرى مع الموارد المحلية وجهاً لوجه من حين لآخر، لكن خطة الاتصالات ستقرر إجراءات الاتصال مثل هذه التي سيتم تنفيذها. بصرف النظر عن هذا ، من المهم أيضًا تسهيل الاتصالات ، ووضع توقعات واضحة داخل الفريق، وتطوير بروتوكولات لحل النزاعات ، بما في ذلك الأشخاص من جميع المواقع في اتخاذ القرار، وفهم الاختلافات الثقافية ، ومشاركة الفضل أثناء النجاح كتاب البمبوك الاصدار السادس- صفحة رقم 333", options: [{ option: "تحسين خطة الاتصالات الخاصة بالمشروع", answer: "true" }, { option: "عقد اجتماعات متكررة مع الخبراء الموجودين فى اماكن جغرافية مختلفة.", answer: "false" }, { option: "تبادل مواقع الخبراء مع الفريق المحلى.", answer: "false" }, { option: "أحضر الخبراء الموجودين فى اماكن مختلفه إلى المكتب ليوم واحد كل شهر لمقابلة باقى الفريق وجها لوجه.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يلاحظ مدير المشروع أن هناك مشاركة ومساهمة محدودة من عضو جديد في الفريق. ماذا يجب أن يفعل مدير المشروع؟", description: "يجب على مدير المشروع أولاً )الانضباط - التصحيح – التوجيه( عضو الفريق الجديد بسبب مشاركته ومساهمته المحدودة. الخيار (مراجعة أدوار ومسؤوليات كل عضو في الفريق(. غير صحيح لأن مدير المشروع يلاحظ أن هناك مشاركة ومساهمة محدودة من عضو جديد في الفريق فقط وليس كل أعضاء الفريق يجب أن يكون مدير المشروع استباقيًا لذلك الخيار)ناقش الموقف مع راعي المشروع اكتب شكوى إلى قسم الموارد البشرية) وليس الممارسات الجيدة كخطوة أولى.", options: [{ option: "مناقشة الموضوع راعي المشروع.", answer: "false" }, { option: "توجيه وتصحيح عضو الفريق الجديد.", answer: "true" }, { option: "كتابة شكوى إلى قسم الموارد البشرية.", answer: "false" }, { option: "مراجعة أدوار ومسؤوليات كل أعضاء فريق المشروع .", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أنت مدير مشروع بنية تحتية . أحد المعنيين الغير معرفين غيرراضى عن أداء مشروعك. لقد طلب بعض التحديثات تتم على المشروع. كمدير مشروع ، ماذا يجب أن تفعل بعد ذلك؟", description: "الكلمة المفتاحية )أحد المعنيين الغير معرفين(يجب عليك أولاً تحديث سجل المعنيين.", options: [{ option: "تنفيذ التغيير", answer: "false" }, { option: "تحديث سجل المعنيين .", answer: "true" }, { option: "تحليل أثر طلب التغيير على المشروع.", answer: "false" }, { option: "إرسال طلب التغيير الى لجنة التحكم في التغيير ((CCB", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أثناء المشروع الذي يؤثر على المنظمة بأكملها ، يبدو أن لدى بعض مجموعات المعنيين توقعات منخفضة للمنتج. ونتيجة لذلك، فإنهم لا يؤدون كما هو متوقع. هذا قد يعرض أهداف المشروع للخطر. ماذا يجب أن يفعل مدير المشروع؟", description: "تفسير الكلمات المفتاحية ( يبدو أن المعنيين لديهم توقعات منخفضة للمنتج) ... تعد خطة شاركة المعنيين أحد مكونات خطة إدارة المشروع التي تحدد الاستراتيجيات والإجراءات المطلوبة لتعزيز المشاركة المثمرة للمعنيين في صنع القرار والتنفيذ يمكن أن تكون رسمية أو غير رسمية ومفصلة للغاية أو مؤطرة على نطاق واسع ، بناءً على احتياجات المشروع وتوقعات أصحاب المصلحة. قد تتضمن خطة إشراك أصحاب المصلحة ، على سبيل المثال لا الحصر ، استراتيجيات أو مناهج محددة للمشاركة مع الأفراد أو مجموعات أصحاب المصلحة صفحة PMBOK السادسة ، 522", options: [{ option: "اطلب من راعي المشروع التدخل وتحفيز المعنيين ذوي الأداء الضعيف.", answer: "false" }, { option: "التحدث مع المعنيين الذين يعانون من مشاكل لزيادة أدائهم.", answer: "false" }, { option: "اتباع خطة مشاركة المعنيين.", answer: "true" }, { option: "إرسال طلب التغيير.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أنت مشغول بإنجاز مهم ، وبعد ذلك ، عليك تقديم تسليم إلى العميل. نظرًا لأن العميل معروف بسلوكه الصعب، فإن الإدارة تشعر بالقلق بشأن ما إذا كان العميل سيقبل التسليم. لذلك، يطلبون منك التحقق من معايير قبول المنتج مرة أخرى قبل الاتصال بالعميل. ما المستند الذي ستراجعه للحصول على هذه المعلومات؟", description: "في بيان نطاق المشروع ، يوجد معايير قبول المنتج البمبوك الاصدار السادس- صفحة رقم 154", options: [{ option: "هيكل تجزئة العمل", answer: "false" }, { option: "بیان نطاق المشروع", answer: "true" }, { option: "خرائط المراقبة", answer: "false" }, { option: "قاموس هيكل تجزئة العمل", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أثناء مشروع تطوير منتج جديد ، يحدد مدير المشروع أن المتطلبات مفاهيمية وأنه من الصعب الحصول على فهم مشترك من قبل جميع المعنيين . ما الذي يمكن أن يفعله مدير المشروع للحصول على فهم مشترك لمتطلبات المشروع؟", description: "الكلمة المفتاحية (المتطلبات مفاهيمية( النماذج الأولية هي طريقة للحصول على ملاحظات مبكرة حول المتطلبات من خلال توفير نموذج للمنتج المتوقع قبل بنائه فعليًا. ومن الأمثلة على النماذج الأولية المنتجات الصغيرة أو النماذج ثنائية وثلاثية الأبعاد التي يتم إنشاؤها بواسطة الكمبيوتر أو النماذج أو المحاكاة تسمح النماذج الأولية لأصحاب المصلحة بتجربة نموذج للمنتج النهائي بدلاً من الاقتصار على مناقشة التمثيلات المجردة لمتطلباتهم. لذلك يمكن لمدير المشروع الحصول على فهم مشترك لمتطلبات المشروع من خلال بناء نموذج أولي البمبوك الاصدار السادس- صفحة رقم 147", options: [{ option: "عقد جلسات عصف ذهني مع المعنيين.", answer: "false" }, { option: "إجراء المتطلبات التفصيلية.", answer: "false" }, { option: "المقارنة مع المؤسسات الأخرى.", answer: "false" }, { option: "بناء نموذج أولي.", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "قام العميل بتوقيع عقد مع مؤسستك لتطوير ترقية المنتج لهم لأن منتجهم الحالي لا يساعد عملياتهم بسبب التغييرات في بيئة الأعمال. يمكنك تحديد موعد اجتماع وإدراك أنهم غير قادرين على توضيح متطلباتهم بوضوح. ما هو النهج الذي ستستخدمه لجمع المتطلبات؟", description: "شرح: عندما لا يتمكن المعنيين من توضيح متطلباتهم، فلن تنجح المقابلات وورش العمل وإنشاء مخطط السياق. سيكون الملاحظة او الحوار نهجا أفضل.", options: [{ option: "تنسيق الأعمال.", answer: "false" }, { option: "الملاحظة او الحوار", answer: "true" }, { option: "مخطط السياق", answer: "false" }, { option: "المقابلات", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "هناك مجموعة متنوعة من المعنيين في المشروع من عدة مجالات وظيفية مختلفة. أثناء تجميع المتطلبات من كل منهم، تريد تحديد المتطلبات متعددة الوظائف وتسوية اختلافات المعنيين بالمشروع. ما هي أفضل أساليب جمع المتطلبات المستخدمة لتحقيق هذا الهدف؟", description: "يُستخدم تنسيق الأعمال مع جلسات العمل المركزة التي تجمع المعنيين الأساسيين معًا لتحديد متطلبات المنتج. ويمكن استخدام تنسيق الأعمال من أجل سرعة تحديد المتطلبات متعددة الوظائف وتسوية الخلافات بين المعنيين. وبسبب طبيعتها الجماعية التفاعلية يمكن أن تساعد الجلسات المنسقة بطريقة جيدة في بناء الثقة وتعزيز العلاقات وتحسين الاتصال بين المشاركين، مما يؤدي إلى زيادة نسبة الإجماع على الآراء بين المعنيين. فضلاً عن ذلك، يمكن اكتشاف الإشكالات مبكرًا وحلها على نحو أسرع عما يحدث في الجلسات الفردية. كتاب البمبوك الاصدار السادس- صفحة رقم 145.", options: [{ option: "العصف الذهني", answer: "false" }, { option: "المقابلات", answer: "false" }, { option: "المجموعات التخصصية.", answer: "false" }, { option: "تنسيق الأعمال", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أثناء تطوير خطة إدارة النطاق ، يطلب أحد المعنيين إضافة تقنية تجريبية إلى نطاق المشروع للمساعدة في تسويق المنتج. ماذا يجب أن يفعل مدير المشروع؟", description: "يجب على مدير المشروع تضمين المعنيين في اجتماعات تخطيط النطاق. لاحظ أن الاجتماع هو أحد الأدوات والتقنيات في عملية وضع خطة إدارة المنتج وذلك لتطوير خطة إدارة النطاق. كتاب البمبوك الاصدار السادس- صفحة رقم 136.", options: [{ option: "استخدم احتياطي المشروع للتكنولوجيا التجريبية.", answer: "false" }, { option: "زيادة ميزانية المشروع بتكلفة التكنولوجيا التجريبية.", answer: "false" }, { option: "مراجعة خط الأساس للنطاق مع المعنيين.", answer: "false" }, { option: "تضمين المعنيين في اجتماعات التخطيط لنطاق المشروع.", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "ينصب التركيز الأساسي للمشروع على الالتزام بالجدول الزمني لمراقبة ذلك ، يطلب مدير المشروع تقارير مرحلية شهرية من المقاول. ما الذي يجب أن يطلب مدير المشروع من المقاول تضمينه في هذه التقارير؟", description: "يجب أن يطلب مدير المشروع من المقاول تضمين هذه التقارير مخططات جانت المعروفة أيضًا باسم المخططات الشريطية لأنها تمثل معلومات الجدول الزمني حيث يتم سرد الأنشطة على المحور الرأسي ، ويتم عرض التواريخ على المحور الأفقي، ويتم عرض فترات النشاط كأشرطة أفقية يتم وضعها وفقًا لتواريخ البدء والانتهاء من السهل نسبيًا قراءة المخططات الشريطية وهي شائعة الاستخدام. كتاب البمبوك الاصدار السادس- صفحة رقم 17 2017 PMBOK 6th edition page", options: [{ option: "خصائص الجدول.", answer: "false" }, { option: "مخطط جانت او المخططات الشريطية.Gantt chart.", answer: "true" }, { option: "مخطط الشبكة.", answer: "false" }, { option: "هيكل تجزئى العمل (((WBS)", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "جاك هو مدير مشروع في مرحلة التنفيذ الان . يريد راعي المشروع إضافة خصائص جديدة لم يتم تحديدها أثناء مرحلة تجميع المتطلبات الأولية. مطلوب 15000 دولار أمريكي لإضافة هذه الخصائص . بناءً على البيانات التالية، التكلفة المباشرة المقدرة للمشروع هي 200،000 دولار أمريكي ، وتقدر إعادة العمل بـ 15,000 دولار أمريكي، واحتياطي الإدارة 20،000 دولار أمريكي) ، فماذا يجب أن يفعل جاك؟", description: "ستتطلب الخصائص الجديدة إضافة 15000 دولار أمريكي، لذلك سيتأثر خط الأساس للتكلفة بسبب هذا التغيير. لذا يجب على مدير المشروع إصدار طلب تغيير لزيادة ميزانية التكلفة بمقدار 15000 دولار أمريكي. سؤال مهم جدا", options: [{ option: "إصدار طلب تغيير لزيادة التكلفة بمقدار 15000 دولار أمريكي", answer: "true" }, { option: "يجب أن يستخدم مدير المشروع 15000 دولار أمريكي لإعادة العمل.", answer: "false" }, { option: "طلب موافقة الإدارة لزيادة ميزانية المشروع بمقدار 15000 دولار أمريكي", answer: "false" }, { option: "يجب أن يستخدم مدير المشروع 15000 دولار أمريكي من احتياطيات الإدارة.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "بدأ للتو مدير مشروع يعمل مع شركة تتجنب المخاطر. يشعر راعي المشروع بالقلق الشديد من أن المشروع قد يتجاوز الميزانية ويتطلع إلى مدير المشروع للتأكيد على أن هذا لن يحدث من أجل التخفيف من مخاوف الراعي ، ما الذي يجب على مدير المشروع فعله؟", description: "يجب على مدير المشروع أولاً تحديد المخاطر وتحديث سجل المخاطر ثم تحليل المخاطر .... تحليل ((SWOT) هو أحد أساليب في تحديد عملية المخاطر لتحديد نقاط القوة والضعف في المنظمة، مع التركيز على المشروع أو المنظمة ، أو مجال العمل بشكل عام. ثم يحدد تحليل SWOT أي فرص للمشروع قد تنشأ عن نقاط القوة وأي تهديدات ناتجة عن نقاط الضعف. يفحص التحليل أيضًا الدرجة التي قد تعوض بها نقاط القوة التنظيمية التهديدات ويحدد ما إذا كانت نقاط الضعف قد تعيق الفرص 41 PMBOK 6th edition page", options: [{ option: "استخدام تحليل البيانات التاريخية.", answer: "false" }, { option: "إجراء تحليل لنقاط القوة والضعف والفرص والتهديدات(SWOT)", answer: "true" }, { option: "إنشاء مصفوفة الاحتمالات والتأثير.", answer: "false" }, { option: "تشغيل محاكاة مونت كارلو.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: " يريد مدير المشروع تحديث أحد عقود موردي المشروع لتلبية الاحتياجات الحالية للمشروع. ومع ذلك ، قبل طلب أي تغيير يجب أن يعرف مدير المشروع العملية الصحيحة لإدراج تغيير في هذا العقد المحدد ماذا يجب ان يفعل اولا؟", description: "يجب أن تكون الخطوة الأولى هي جمع معلومات كافية حتى يمكن تحديد القرار الصحيح ومسار العمل. يجب أن تكون الخطوة الثانية هي إصدار طلب التغيير واتباع الموافقة عليه. يجب على مدير المشروع أولاً مراجعة إجراء التحكم في التغيير الخاص بالعقد والذي يمكن العثور عليه في عقد الشراء نفسه كتاب البمبوك الاصدار السادس- صفحة رقم 489", options: [{ option: "مراجعة إجراءات لتسوية المنازعات الموجودة في العقد", answer: "false" }, { option: "إصدار طلب التغيير واعتماده من خلال عملية إجراء التحكم المتكامل في التغيير.", answer: "false" }, { option: "تحديث خطة إدارة المشتريات", answer: "false" }, { option: "مراجعة إجراءات التحكم فى التغيير كما هو موثق في العقد", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أنت مدير مشروع تكنولوجيا المعلومات تبدأ أنت والمعنيين الرئيسيين في إجراء تحليل المنفعة للمشروع . بسبب الحلول التي لم يتم تحديدها ، أبلغك مهندس من قسم آخر بوجود أنظمة متأثرة. كمدير مشروع ، ماذا يجب أن تفعل؟", description: "سجل الإشكالات هو مستند مشروع يُستخدم لتحديد المشكلات التي تحدث في المشروع ومراقبتها يمكن تحديث سجل الإشكالات بأي مشكلات جديدة يتم العثور عليها أثناء المشروع ووصفها في السيناريو بسبب الحلول التي لم يتم تحديدها والتي تسبب تأثر النظام. جعل هذا الاختيار أفضل إجابة على السؤال المطروح.", options: [{ option: "قم بمراجعة تحليل المنفعة للمشروع للنظر في التأثير الإضافي", answer: "false" }, { option: "قم بتوثيق المشكلة في سجل الإشكالات", answer: "true" }, { option: "تحديد جميع المعنيين", answer: "false" }, { option: "تحديث سجل المخاطر", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أنت مدير مشروع في مرحلة تنفيذ مشروع بناء سكني. المشروع متأخر عن الجدول الزمني ويفقد بعض المواد. يقدم المقاول عرضا لتوريد المواد المفقودة وسداد التكلفة نظرًا لأن عملية الشراء الداخلية ستتسبب في مزيد من التأخير. أنت لا توافق على التكاليف المقدمة من قبل المقاول للمواد المفقودة. كمدير مشروع ، ماذا يجب أن تفعل بعد ذلك؟", description: "الهدف من تحديث استراتيجية الشراء هو تحديد طريقة تسليم المشروع ، ونوع الاتفاقية الاتفاقيات الملزمة قانونًا ، وكيفية تقدم الشراء خلال مراحل الشراء. ولذلك يجب على مدير المشروع تحديث استراتيجية الشراء\nوالتفاوض مع المقاول", options: [{ option: "مطالبة المقاول بمراجعة العرض وتقليل التكاليف.", answer: "false" }, { option: "تحديث خطة إدارة المشتريات والتفاوض مع المقاول.", answer: "false" }, { option: "تحديث استراتيجية الشراء والتفاوض مع المقاول", answer: "true" }, { option: "قابل راعي المشروع واطلب تخصيص المزيد من الميزانية لتغطية التكلفة.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يشعر بعض أعضاء فريق المشروع بالإحباط بسبب عضو جديد في الفريق عمل سابقا في بيئة سكروم. يتواصل عضو الفريق الجديد هذا مع أعضاء فريق المشروع على أساس يومي فيما يبدو أنه محاولة للانخراط في اجتماعات فردية ماذا يجب أن يفعل مدير المشروع؟", description: "يبدوان فريق المشروع كان يعمل بالمنهجيه التقليديه ولذلك شهر بالاحباط عندما طلب عضو الفريق الجديد بالاجتماع اليومى لذلك يجب على مدير المشروع الاجتماع مع الفريق ومراجعة خطة إدارة الاتصالات.", options: [{ option: "أرسل بريدًا إلكترونيًا إلى فريق المشروع يحدد استراتيجية الاتصال.", answer: "false" }, { option: "وجه فريق المشروع لمواصلة المناقشة مع عضو الفريق الجديد.", answer: "false" }, { option: "حدد موعدًا لاجتماع فريق المشروع لمراجعة خطة إدارة الاتصالات.", answer: "true" }, { option: "قم بتوجيه عضو الفريق الجديد إلى المنهجية التي يستخدمها فريق المشروع.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أثناء إدارة نطاق المشروع ، لاحظ مدير المشروع أن بعض المتطلبات تبدو مختلفة عن تلك المحددة في ميثاق المشروع. ماذا يجب أن يفعل مدير المشروع؟", description: "المشروع في مرحلة التخطيط ، لذلك لم يتم إنشاء خط أساس بعد . لذا فإن الخيارات إرسال التغيير إلى لجنة التحكم في التغيير للموافقة عليها وتعديل خط الأساس للمشروع لتغييرات التأثير الطفيفة على نطاق المشروع)غير صحيحة مجموعة التركيز وجلسات العصف الذهني هي أدوات وتقنيات مستخدمة في إدارة النطاق.", options: [{ option: "يستخدم المجموعات التخصصية وجلسات العصف الذهنى لتجميع تفاصيل اكثر عن نطاق المشروع.", answer: "true" }, { option: "إرسال طلب التغيير الى لجنة التحكم فى التغيير للموافقة والاعتماد", answer: "false" }, { option: "تعديل خط الأساس للمشروع لإجراء تغييرات طفيفة التأثير على نطاق المشروع", answer: "false" }, { option: "التوضيح للعميل أن هذه الطلبات قد تؤثر على الجدول الزمنى وتكلفة المشروع .", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "فريق سكروم في منتصف التكرار . يطلب راعي المشروع من مدير منهجية اسكرم أن يتم تطوير متطلبات إلزامية جديدة وعاجلة وربما معقدة مرتبطة بالامتثال على الفور في التكرار الحالي. ويؤكد الراعي أنه إذا لم يتم تنفيذ المطلب ، فقد يتم إلغاء المشروع بأكمله. ما هو أفضل رد لمدير منهجية اسكرم ؟", description: "مالك المنتج هو السلطة المناسبة لمراجعة المتطلبات الجديدة والبت ، جنبا إلى جنب مع الفريق، ما إذا كان يجب تضمين المتطلبات في سجل المنتج وفي أي تكرار قد يقرر مالك المنتج إلغاء التكرار الحالي وبدء تكرار سريع جديد يتضمن هذا المطلب الجديد ذي الأولوية العالية في هذه الحالة، من المحتمل أن يحتاج الفريق إلى وقت لتقييم تأثير المتطلبات الإلزامية الجديدة المتعلقة بالامتثال يجب أن يناقش سكرم مستر المتطلبات الجديدة مع مالك المنتج الذي قد يقرر ، مع الفريق، إلغاء التكرار في هذه الحالة، سيتعين على مالك المنتج والفريق إعادة تخطيط العمل لهذا التكرار والتكرارات المستقبلية", options: [{ option: "إرسال طلب تغيير لتحديث خط أساس النطاق وإضافة المتطلبات إلى مصفوفة إمكانية تتبع المتطلبات", answer: "false" }, { option: "اضافة المتطلبات إلى سجل اعمال المنتج ومطالبة مالك المنتج مراجعة المتطلبات بعد انتهاء التكرار.", answer: "false" }, { option: "اضافة المتطلب إلى سجل منتج الخاص بالتكرار الحالي وتوجيه الفريق لبدء العمل عليه على الفور.", answer: "false" }, { option: "مناقشة المتطلبات مع مالك المنتج ، ودعه يتخذ قرارًا ، بما في ذلك إلغاء التكرار.", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "هناك خلاف بين المعنيين في المشروع يجعل مدير المشروع يواجه صعوبة في الحصول على الموافقة على متطلبات المشروع بصفتك مدير مشروع ، ما الذي يجب عليك فعله أولاً لتسهيل الموافقة على متطلبات المشروع؟", description: "يجب على مدير المشروع تحديد سبب الخلاف بين المعنيين فى المشروع لتسهيل الموافقه على متطلبات المشروع.", options: [{ option: "أنشطة بناء الفريق", answer: "false" }, { option: "تحديد مصدر الخلاف", answer: "true" }, { option: "إجراء تحليل المعنيين", answer: "false" }, { option: "مراجعة ميثاق المشروع", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "تم تعيين مدير مشروع فني حديثا من قبل العميل. يزور مدير المشروع الفني موقع المشروع للتحقق من نواتج المشروع، ويشعر المدير الفني أن مدير المشروع لم يقدم معلومات كافية بصفتك مدير مشروع ، ما الذي يجب عليك فعله أولاً لحل هذا الموقف؟", description: "الكلمة المفتاحية (مدير المشروع الفني الجديد ( لذلك يجب على مدير المشروع مراجعة وتحديث خطة مشاركة المعنيين لتعكس المدير الفني الجديد ومستوى المشاركة الحالى والمرغوب . تحتوي خطة إشراك أصحاب المصلحة على طريقة لتحديث وتنقيح خطة إشراك أصحاب المصلحة", options: [{ option: "تحديث سجل المخاطر", answer: "false" }, { option: "عمل اجتماعات بشكل مستمر مع المدير الفنى", answer: "false" }, { option: "تحديث سجل المشاكل والتصعيد لراعى المشروع", answer: "false" }, { option: "مراجعة وتحديث خطة مشاركة المعنيين", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "آدم هو مدير المشروع الذي يبدأ مشروعًا باستخدام نهج هجين ويلاحظ أن بعض أعضاء الفريق لديهم معرفة فقط بالأساليب التنبؤية. ما الذي يحتاجه آدم أولاً من أجل الحصول على أداء جيد للمشروع؟", description: "تقييم التدريب المطلوب لكل عضو في الفريق.", options: [{ option: "عمل تدريب لكل أعضاء الفريق على النهج الهجين", answer: "false" }, { option: "استبدال الفريق بخبراء في النهج الهجين", answer: "false" }, { option: "تقييم التدريب المطلوب لكل عضو في الفريق.", answer: "true" }, { option: "ارشاد وتوجية وتدريب أعضاء الفريق الذين يفتقرون إلى المعرفة للنهج الهجين", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أثناء تطوير الجدول الزمني لمشروعك ، طلبت من الجميع تقديم قائمة بالإجازات المخططة خلال الأشهر الثلاثة المقبلة، وقمت بتطبيق هذه المعلومات على المهام التي يكون كل شخص مسؤولاً عنها. ما الذي يحتاج أيضًا إلى التحديث؟", description: "تشير تقويمات الموارد إلى متى يمكن أن تعمل الموارد في المشروع. يجب تحديث وثيقة المشروع هذه لتعكس وقت العمل وغير وقت العمل لموارد المشروع. 323 PMBOK® Guide 6th edition, Page]", options: [{ option: "تقويم الموارد", answer: "true" }, { option: "Lags فترات التأخير", answer: "false" }, { option: "فترات السبق Leads", answer: "false" }, { option: "مخطط جانت - Gantt chart", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "لقد طلب منك تطوير موقع ويب جديد لأحد عملائك. يجب أن يكون موقع الويب مستقلاً عن المتصفح ويتم تسليمه في غضون شهر. على الرغم من أن تصميم موقع الويب بسيط ، إلا أن شرط استقلالية المتصفح قد أضاف بعض التعقيد إلى المشروع. أي من الأساليب التالية توصي به لهذا المشروع؟", description: "يتطلب المشروع تسليم نهائي واحد ومع ذلك، قد يتطلب عمل المشروع عمليات التكرار حتى يتم دعم جميع المتصفحات النهج التكراري مثالي في هذه الحالة", options: [{ option: "النهج التكراري", answer: "true" }, { option: "النهج الرشيق", answer: "false" }, { option: "النهج المتزايد", answer: "false" }, { option: "النهج التقليدي او التنبؤى", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أي من الأساليب التالية المستخدمة في دورات الحياة التكيفية تساعد في تقليل تسيب النطاق؟", description: "الفترات الزمنية المحددة هي الفترات التي يعمل خلالها الفريق بثبات نحو تحقيق الهدف. يساعد ضبط الوقت على تقليل زحف النطاق إلى الحد الأدنى حيث يجبر الفرق على معالجة الميزات الأساسية أولا ، ثم الميزات الأخرى عندما يسمح الوقت بذلك", options: [{ option: "مالك المنتج", answer: "false" }, { option: "التكرارات", answer: "false" }, { option: "قصص المستخدم", answer: "false" }, { option: "الفترات الزمنية المحددة", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أثناء تنفيذ المشروع ، قدم العميل متطلبات جديدة، والتي من الواضح أنها ستتطلب فقط تعديلا طفيفًا على هيكل تنظيم العمل(WBS). ما الذي ينبغي عليك فعله اولا؟", description: "هيكل تجزئة العمل هو أحد مكونات خط الأساس للتطابق. يلزم طلب تغيير معتمد لتحديث أي من خطوط الأساس للمشروع بما في ذلك مكوناتها.", options: [{ option: "تعديل هيكل تجزئة العمل حسب الضرورة لأن التغيير طفيف", answer: "false" }, { option: "رفض الطلب لأنه يمثل تسيب النطاق", answer: "false" }, { option: "تقديم طلب تغيير لتحديث الخط المرجعي للنطاق", answer: "true" }, { option: "إرسال طلب تغيير لتحديث خط الأساس للجدول الزمني", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أنت تقود مشروعًا لتصميم نظام جديد ومبتكر يعتمد على التكنولوجيا المطورة حديثًا. في المشاريع السابقة، باستخدام نهج تقليدي لإدارة مشروع الشلال ، واجهت مشكلات مع زحف النطاق. ومع ذلك ، نظرًا لطبيعة المشروع الحالي، فقد قررت استخدام نهج رشيق من حيث صلته بنطاق المشروع ، كيف سيختلف هذا المشروع عن مشاريعك السابقة؟", description: "يمكن اختيار إطار عمل رشيق بدلاً من نهج إدارة مشروع الشلال التقليدي عندما لا يكون نطاق المشروع مفهومًا جيدًا مقدمًا. وبدلاً من ذلك ، سيتطور نطاق المشروع أثناء سير المشروع كما تم تحديده وإعادة تعريفه.", options: [{ option: "أثناء التخطيط الأولي للمشروع، سيتم إنشاء هيكل تنظيم عمل أكثر تفصيلاً", answer: "false" }, { option: "سيتم فهم نطاق المشروع بشكل أفضل في بداية المشروع", answer: "false" }, { option: "سيتم تجنب زحف النطاق من خلال نظام صارم لإدارة التغيير", answer: "false" }, { option: "سيتطور نطاق المشروع خلال المشروع", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أكمل فريق مشروعك مؤخرًا التكرار الثالث من المشروع. حتى الآن تم تسليم 45 من قصص المستخدم بنجاح إلى العميل بالنسبة لهذا المشروع ، حجم التكرار هو ثلاثة أسابيع. بعد مراجعة سجل اعمال المنتج ، لديك 150 قصة مستخدم متبقية ليتم تسليمها. ما هي السرعة الحالية للمشروع؟", description: "السرعة - متوسط نقاط القصص لكل تكرار نظرًا لأن الفريق قد قدم 45 قصة في ثلاث تكرارات . فإن السرعة في 45/3 - 15 نقطة قصة لكل تكرار [دليل الممارسة الرشيقة، الطبعة الأولى ، الصفحة (61)", options: [{ option: "10", answer: "false" }, { option: "30", answer: "false" }, { option: "15", answer: "true" }, { option: "1", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "قرر مدير المشروع والفريق أن المشروع سيتم تقسيمه إلى تكرارات مدتها خمسة أسابيع مع أسبوعين فيما بينهما للاستعراضات السابقة وتخطيط التكرار. أين يجب توثيق هذا النهج ؟", description: "توثق خطة إدارة الجدول الزمني الترتيب والتصاميم المرغوبة للجداول التي سيتم استخدامها في المشروع يتضمن ذلك المقاطع المقيدة زمنيًا التي وافق عليها فريق أجايل Sixth Edition, Page(s) 182 - (PMBOK Guide", options: [{ option: "قائمة الانشطة", answer: "false" }, { option: "خطة إدارة الجدول الزمنى", answer: "true" }, { option: "سجل اعمال المنتج", answer: "false" }, { option: "ميثاق المشروع", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أنت تخطط لأنشطة إدارة المخاطر لمشروع صغير تقوده. يواجه فريق المشروع مشكلة في تعيين تأثيرات التكلفة والجدول الزمني على المخاطر المحددة بسبب نقص البيانات الرقمية عالية الجودة ماذا تفعل كمدير مشروع ؟", description: "يعطي التحليل النوعي للمخاطر الأولوية للمخاطر من خلال احتمالية حدوثها وتأثيرها مما يسمح للفريق بتركيز الجهود على المخاطر ذات الأولوية العالية من خلال محاولة تعيين تأثيرات التكلفة على المخاطر، فأنت تحاول استخدام التحليل الكمي للمخاطر الذي لا يناسب جميع المشاريع. يعتمد التحليل الكمي للمخاطر على توافر بيانات عالية الجودة لمخاطر المشروع. نظرًا لأنك تكافح من أجل الحصول على مثل هذه البيانات ، فسيكون التحليل النوعي للمخاطر كافياً Sixth Edition, Page(s) 428 - (PMBOK Guide", options: [{ option: "قم بتكوين بيانات رقمية وتقييمها على أساس كمي", answer: "false" }, { option: "حرر فريق المشروع وقم بإنهاء المشروع", answer: "false" }, { option: "اطلب البيانات الرقمية من راعي المشروع", answer: "false" }, { option: "انتقل مباشرة إلى خطة عملية الاستجابة للمخاطر", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أنت مدير مشروع، حيث لديك اثني عشر مبرمجا اثنان منهم قد سلموا للتو خطابات استقالتهما. تم تحديد هذه المخاطر المحققة واحتسابها خلال مرحلة التخطيط للمشروع. إذا لم يتم اتخاذ أي إجراء، فلن تلتزم بالمواعيد النهائية. ماذا يجب ان تفعل بعد ذلك؟", description: "سجل المخاطر يحتوى على استجابات المخاطر المتفق عليها لكل خطر فردي والمالكين المعينين لكل خطة استجابة. الشيء التالي الذي عليك القيام به هو مراجعة سجل المخاطر حتى تتمكن من تنفيذ الاستجابة للمخاطر المتفق عليها 450 (PMBOK - Sixth Edition, Page(s", options: [{ option: "ضغط الجدول الزمني", answer: "false" }, { option: "عمل طلب تغيير لتحديث خط الاساس للجدول الزمني", answer: "false" }, { option: "تطبيق عملية وضع خطة الاستجابة للمخاطر", answer: "false" }, { option: "مراجعة سجل المخاطر", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "مارك يدير مشروع أتمتة العمليات الروبوتية. تم تحديد عدد من مخاطر المشروع خلال اجتماع استعراض المخاطر و تم التخطيط لاستراتيجيات إدارة المخاطر المقابلة. يريد مارك الآن تعيين مالك مخاطرة لكل خطر من مخاطر المشروع يمثل خطرًا عمل الاستجابة المخطط أين يجب عليه تحديث هذه المعلومات؟", description: "يتم توثيق أصحاب المخاطر المعينين في سجل المخاطر. 13 PMBOK® Guide 6th edition, Page]", options: [{ option: "سجل المخاطر", answer: "true" }, { option: "ميثاق المشروع", answer: "false" }, { option: "سجل المعنيين", answer: "false" }, { option: "خطة إدارة الموارد", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "بدون الاهتمام بأعلى قيمة للعميل ، قد يقوم فريق اجايل بإنشاء ميزات لا يتم تقديرها أو غير ذلك ذات قيمة غير كافية، وبالتالي إضاعة الجهد. كيف يقوم فريق اجايل بتخفيف هذه المخاطر؟", description: "من خلال إنشاء سجل اعمال المنتج بمساعدة مالك المنتج. 1 Agile Practice Guide, 1st edition, Page", options: [{ option: "من خلال إنشاء سجل اعمال المنتج بمساعدة مالك المنتج.", answer: "true" }, { option: "من خلال إنشاء سجل اعمال المنتج بمساعدة مدير المشروع.", answer: "false" }, { option: "من خلال إنشاء خطة إدارة المشروع التفصيلية بمساعدة مدير المشروع.", answer: "false" }, { option: "من خلال إنشاء خطة إدارة المشروع التفصيلية بمساعدة مالك المنتج.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أي من الممارسات التالية يُنصح باستخدامها الشركة الموردة أثناء التعاقد مع عميل لمشروع رشيق؟", description: "يجب تحديد متطلبات مشاركة العملاء بوضوح في العقد لأن اجايل تعتمد على التعليقات المستمرة ومشاركة العميل", options: [{ option: "إضافة بند يسمح للمشتري بإلغاء العقد إذا فشل البائع في التسليم كما هو متفق عليه", answer: "false" }, { option: "الحاجة إلى مشاركة العملاء", answer: "true" }, { option: "استخدام عقود النموذج القياسية الموصى بها من قبل مكتب إدارة المشروع", answer: "false" }, { option: "الإصرار على التدخل القانوني", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "الإنتاج التسليمات ، يجب أن يتعاقد مشروعك مع عدة بائعين سيتم تطوير المخرجات باستخدام إطار عمل رشيق مع تسليم مدفوع بالقيود ، مما سيؤثر على العلاقة التعاقدية مع البائعين. أي من الاستراتيجيات التالية هي الأنسب لهذا المشروع؟ (اختر ثلاثة)؟", description: "يصف السؤال مشروعًا سيستخدم منهجيات رشيقة بما في ذلك التسليم المدفوع بالقيود التسليم المدفوع بالفيد هو إستراتيجية شائعة للرشاقة المشاريع، حيث يتم إصلاح التكلفة و / أو الوقت. ويسمح للنطاق بالتنوع. سوف تتطلب مثل هذه المشاريع المزيد من المرونة وعلاقة تعاون أكثر مع الباعة بالمقارنة مع نهج الشلال التقليدي. تمثل اختيارات الإجابة الصحيحة التكتيكات التي تدعم الحاجة إلى التعاون وتوفر المزيد المرونة. تدل اختيارات الإجابات غير الصحيحة على الاستراتيجيات الأكثر ملاءمة للنموذج التنبئي والتي من غير المرجح أن تستخدم في مشروع رشيق.", options: [{ option: "اعتماد زيادات ثابتة السعر عند التعاقد مع البائعين", answer: "true" }, { option: "تابع علاقة المخاطر المشتركة مع البائعين", answer: "true" }, { option: "استخدام عقد ثابت السعر لكل من البائعين", answer: "false" }, { option: "قم بتضمين خيار الإلغاء المبكر في عقود البائعين", answer: "true" }, { option: "تطوير بيان المشتريات للعمل على أساس هيكل تجزئة العمل التفصيلي", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أنت مدير مشروع تعمل عن قرب مع مسؤول المشتريات لديك لضمان إنهاء جميع المطالبات والفواتير والمدفوعات النهائية مع البائع. تم استيفاء شروط وأحكام العقد ، وتم الوفاء بجميع الإنجازات وافقت ماذا يجب ان تفعل بعد ذلك؟", description: "اغلاق المشتريات هي احد مخرجات عملية مراقبة المشتريات التي ينطوي عليها السيناريو. يجب على المشتري أن يزود البائع (البائع) بإشعار رسمي مكتوب بإتمام العقد. 499 (PMBOK Guide - Sixth Edition, Page(s", options: [{ option: "قم بإجراء مراجعة منظمة لعملية الشراء", answer: "false" }, { option: "قم بإجراء الفحص النهائي لمخرجات البائع", answer: "false" }, { option: "إخطار البائع بأن العقد قد اكتمل", answer: "true" }, { option: "إنهاء جميع الأنشطة الخاصة بالمشروع وتحرير فريق المشروع", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "لقد تم تكليفك للتو بمشروع رشيق قادم كجزء من أعمال مرحلة ما قبل المشروع، تقوم بمراجعة جميع سياسات وإجراءات المؤسسة فيما يتعلق بالامتثال. أنت تدرك أن بعضها فقط قد يكون قابلاً للتطبيق على مشروعك. ما الذي ينبغي عليك فعله اولا؟", description: "يصف السيناريو موقفًا تنطبق فيه فقط بعض السياسات والإجراءات المتعلقة بالامتثال لمشروع رشيق. أحد المبادئ الموضحة في بيان اجايل على أن \"البساطة - فن تعظيم حجم العمل غير المنجز - أمر أساسي. \" تمثل أنشطة الامتثال النفقات العامة، والتي لا تقدم قيمة مباشرة. تمشيا مع العقلية المرنة ، يجب على مدير المشروع تقليل النفقات العامة وتضمين فقط أنشطة الامتثال تلك من الضروري يعد تصنيف فئات الامتثال إحدى المهام المرتبطة بمجال بيئة الأعمال الخاص بمتخصص إدارة المشاريع (PMP) مخطط محتوى الامتحان قد يكون تصنيف فئات الامتثال التي تم تسجيلها في سياسات وإجراءات المنظمة مفيدًا في التفريق بين تلك التي تتطلب إنفاق موارد وتلك التي لا تتطلب ذلك. على سبيل المثال ، قد يكون لدى المنظمة إرشادات محددة للامتثال لها قواعد وأنشطة الاتحاد. ومع ذلك ، إذا لم يكن للمشروع قيد التنفيذ مشاركة نقابية ، فيمكن تجاهل فئة الامتثال بأكملها.", options: [{ option: "دمج جميع سياسات وإجراءات الامتثال في استراتيجية التنفيذ", answer: "false" }, { option: "تبديل نهج إدارة المشروع من الرشيق إلى التقليدي", answer: "false" }, { option: "تصنيف فئات الامتثال لتحديد إنفاق موارد المشروع", answer: "true" }, { option: "تأكد من تضمين أنشطة الامتثال في خط الأساس للجدول الزمني للمشروع", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أين يجب أن يحصل مدير المشروع على تحليل التكلفة مقابل المنفعة الأولي؟", description: "تحتوي دراسة الجدوى على تحليل التكلفة مقابل المنفعة الكامل للمشروع. يمكنه تزويد مدير المشروع بجميع المعلومات الأولية المستخدمة لتبرير المشروع", options: [{ option: "خطة إدارة المنافع", answer: "false" }, { option: "من العميل", answer: "false" }, { option: "ميثاق المشروع", answer: "false" }, { option: "دراسة الجدوى", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "لقد تم تعيينك للتو كمدير مشروع لمشروع في شركتك لنشر نظام جديد يختلف أصحاب المصلحة حول أهداف المشروع ونهجه. كمدير مشروع ، ماذا يجب أن تفعل ؟", description: "أفضل إجابة للتعامل مع هذا الموقف وإرضاء أصحاب المصلحة هو تفصيل الموقف لتحديد الأسباب الجذرية للخلاف ثم العمل مع كل صاحب مصلحة وكيان مشارك في أدوارهم ومسؤولياتهم المحددة.", options: [{ option: "قم بإجراء ورش عمل تحديد النطاق مع فريق المشروع لإنشاء هيكل تجزئة العمل ، وتعيين المهام إلى كيانات مختلفة . ومشاركتها مع جميع المعنيين. واطلب من الفريق إكمال مهامهم كما هو مقرر", answer: "false" }, { option: "تجزئة الموقف لتحديد الأسباب الجذرية للخلاف ثم العمل مع كل صاحب مصلحة وكيان مشارك في أدوارهم ومسؤولياتهم المحددة.", answer: "true" }, { option: "إنشاء مصفوفة الأدوار والمسؤوليات بناءً على ميثاق المشروع واطلب من الراعي مشاركته مع جميع أصحاب المصلحة لتوفير سلطة موثوقة.", answer: "false" }, { option: "عقد اجتماعا عامًا مع جميع المعنيين ومراجعة قائمة الأنشطة لكل منهم وتناولها.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "بدأ التكرار منذ يومين. لم يتوصل الفريق إلى توافق في الآراء بشأن كيفية معالجة سمة لمنتج قيد الإنشاء. كمدير مشروع ، ماذا يجب أن تفعل ", description: "الإجابة الصحيحة هي: حدد جلسة لإعطاء الجميع فرصة للتعبير عن آرائهم حول كل فكرة . ثم التصويت واتخاذ القرار يتسم فريق أجايل بالتنظيم الذاتي والتوجيه وتتمثل مسؤوليته الرئيسية في تحديد كيفية معالجة الميزات وتنفيذها وتقديرها.\nالخيار (اطلب من مالك المنتج، وهو صاحب المصلحة الرئيسي ، الاختيار من بين الأفكار المختلفة. غير صحيح بسبب قراره الاستبدادي. يجب توزيع بعض القرارات على فريق أجايل وخاصة كيفية معالجة وتنفيذ المهام الفنيه", options: [{ option: "اطلب من مالك المنتج . وهو صاحب المصلحة الرئيسي . الاختيار من بين الأفكار المختلفة.", answer: "false" }, { option: "اقترح أفكارًا على المجموعة . واطلب من الجميع التصويت . ثم اختر الفكرة الأكثر شيوعًا.", answer: "false" }, { option: "حدد جلسة لإعطاء الجميع فرصة للتعبير عن آرائهم حول كل فكرة ، ثم التصويت والبت.", answer: "true" }, { option: "قدم رأيا حول إيجابيات وسلبيات كل فكرة وحاول الوصول إلى إجماع في اجتماع ميسر", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يطلب أحد المعنيين من اسكرم ماستر ، أن يطلعه على أحدث تقرير أسبوعي عن الحالة للتحقق من صحة المشروع والكمية المتبقية من العمل في المشروع. بدلاً من ذلك ، يأخذ جون صاحب المصلحة إلى حائط الفريق حيث يتم نشر بعض التحديثات ذات الصلة بشكل مستمر. لمعالجة استفسار صاحب المصلحة ، يشير جون تحديدًا إلى", description: "يُظهر مخطط Burndow مقدار العمل الذي تم إكماله في التكرار وإجمالي العمل المتبقي.", options: [{ option: "اتجاه السرعة Velocity trend", answer: "false" }, { option: "سجل المنتج الخاص بالتكرار Sprint backlog", answer: "false" }, { option: "Burndown chart", answer: "true" }, { option: "لوحة كانبان", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "إنتاجية أحد المطورين متدنية مقارنة بالآخرين بسبب مشكلات الحضور المتكررة. ما الذي يجب أن يفعله مدير المشروع لتحديد الإجراءات التأديبية المناسبة الخاصة بهذا المشروع؟", description: "توفر خطة إدارة الموارد إرشادات حول الجوانب المختلفة لتطوير الفريق بما في ذلك الإجراءات التأديبية نتيجة تقييمات الاداء للفريق. 339 PMBOK 6th edition page", options: [{ option: "مراجعة اصول العملية التنظيمية OPA", answer: "false" }, { option: "مراجعة مصفوفة تعيين المسؤليات RACI", answer: "false" }, { option: "تحقق من خطة إدارة الموارد", answer: "true" }, { option: "مراجعة سجل المعنيين", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أنت مدير مشروع برمجيات أنت تعمل مع فريق افتراضي عالمي يواجه الفريق صعوبة في التواصل مع بعضهم البعض وغالبًا ما يفوتهم رسائل مهمة. هذه الصعوبة تؤدي إلى عدم الالتزام بموعد تسليم المشروع. ما الذي يجب عليك فعله لتعزيز فعالية فريق المشروع؟", description: "السبب الجذري لمشكلة الاتصال معروف لأنك تعمل مع فريق افتراضي عالمي والفريق موجود في أماكن مختلفة لتعزيز فعالية فريق المشروع ، يجب عليك تنفيذ وحدة الموقع واجتمع بالفريق في مكان واحد لتحسين الاتصال. .", options: [{ option: "تنفيذ وحدة الموقع واجتمع بالفريق فى مكان واحد لتحسين الاتصال.", answer: "true" }, { option: "بدء الاتصال اليومي لتقدم المشروع.", answer: "false" }, { option: "حدد موعدًا لاجتماع مجلس مراقبة الاتصالات", answer: "false" }, { option: "تحديد السبب الجذري لمشكلة الاتصالات", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "تم تعيين موظف مبتدئ في الفريق مؤخرًا. يخبر عضو الفريق الجديد مدير المشروع أنه مرتبك لانه ليس على دراية بما هو متوقع منه في المشروع. ما الذي يجب على مدير المشروع فعله لمساعدة عضو الفريق الجديد؟", description: "الاجابه الصحيحة : يطلب من عضو الفريق الجديد مراجعة مصفوفة راسى المسؤل والمحاسب والتشاور والإبلاغ قبل طلب المساعدة. مخطط راسي هو أداة مفيدة لاستخدامها لضمان تعيين واضح الأدوار والمسؤوليات عندما يتكون الفريق من موارد داخلية وخارجية.", options: [{ option: "يطلب من عضو الفريق الجديد مراجعة مصفوفة راسى المسؤل والمحاسب والتشاور والإبلاغ قبل طلب المساعدة.", answer: "true" }, { option: "يطلب من عضو الفريق الجديد مراجعة ميثاق المشروع وخطة إشراك أصحاب المصلحة.", answer: "false" }, { option: "ينصح عضو الفريق الجديد بالتسجيل في التدريب على إدارة المشروع.", answer: "false" }, { option: "يقوم بمراجعة خطة إدارة الموارد وتقديم التوجيه لعضو الفريق الجديد إذا لزم الأمر.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "خلال اجتماع تقييم بائر راجعى الخاص بالتكرار الخامس ، يشتكي فريق المشروع من وجود العديد من العقبات مع المنظمة التي تؤدي إلى تأخير المشروع وإعادة العمل. ماذا يجب أن يفعل مدير المشروع في هذه الحالة؟", description: "تعزيز التعاون للمساعدة في إزالة العقبات التي تواجه الفريق.", options: [{ option: "تعزيز التعاون للمساعدة في إزالة العقبات التي تواجه الفريق.", answer: "true" }, { option: "إجراء تحليل السبب الجذري اثناء اجتماعات الفريق اليومية", answer: "false" }, { option: "يقوم بإنشاء تكرار سريع مخصص لحل العقبات مع الفريق.", answer: "false" }, { option: "يقوم بتضمين قرارات العوائق خلال اجتماعات الفريق اليومية.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "بسبب التأخير في بعض الأنشطة. قام أحد أعضاء فريق المشروع بزيادة النطاق دون أي موافقة. ماذا يجب أن يفعل مدير المشروع بعد ذلك؟", description: "يجب على مدير المشروع اولا تقييم آثار التغييرات التي تم إجراؤها على المشروع.", options: [{ option: "يقوم بإزالة التغييرات لتتناسب مع المتطلبات الأصلية.", answer: "false" }, { option: "اضافة أعضاء الفريق إلى المشروع لتجنب المزيد من التأخيرات في الجدول الزمني", answer: "false" }, { option: "تقييم آثار التغييرات التي تم إجراؤها على المشروع", answer: "true" }, { option: "تحديث وثائق المشروع بالنطاق الجديد", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "انتهى مدير المشروع من مراجعة مخرجات المشروع للتكرار الحالي مع العميل يشكو صاحب مصلحة رئيسي جديد انضم للتو إلى فريق العميل ، من أن النتائج لا توفر القدرات التي توقعها العمل. ما الذي يجب أن يفعله مدير المشروع لمعالجة هذه المشكلة؟", description: "إجراء تحديد أصحاب المصلحة لتحديد صاحب المصلحة الرئيسي الجديد وإجراء جلسة مراجعة أهداف المشروع لأن صاحب المصلحة الرئيسي يشكو من أن النتائج لا توفر القدرات التي يتوقعها العمل.\u000bلا يوجد طلب تغيير في أجايل", options: [{ option: "يقوم بإنشاء طلب تغيير وتحديث خطة إدارة النطاق", answer: "false" }, { option: "يقوم بتحديث قائمة أصحاب المصلحة وإصلاح النتائح", answer: "false" }, { option: "إجراء تعريف أصحاب المصلحة وجلسة مراجعة أهداف المشروع", answer: "true" }, { option: "تحديث خطة إدارة المشروع والدروس المستفادة", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "ما الإجراءان اللذان يجب على مدير المشروع مراعاتهما أثناء تعيين فريق افتراضي جديد؟ اختر اجابتين", description: "الاجابة الصحيحة :\nحدد في البداية كيف سيتم رصد التقدم وأفضل الوسائل لإبلاغ التقدم\nالتحقق مما إذا كان أعضاء الفريق في أكثر من فريق ولا يمكن تخصيص الوقت الكافي لهذا الفريق\nسؤال مهم جدا للاختبار الحقيقي", options: [{ option: "يقوم بتقييم نقاط القوة والضعف لدى مدير المشروع كقائد افتراضي للفريق لتحديد العوائق التي يمكن تجنبها", answer: "false" }, { option: "تحديد أنواع وكميات الموارد المطلوبة لكل حزمة عمل أو نشاط", answer: "false" }, { option: "حدد في البداية كيف سيتم رصد التقدم وأفضل الوسائل لإبلاغ التقدم", answer: "true" }, { option: "تحقق مما إذا كان أعضاء الفريق في أكثر من فريق ولا يمكن تخصيص الوقت الكافي لهذا الفريق", answer: "true" }, { option: "يقوم يتعيين أفضل الموارد داخل المنظمة لتكون جزءًا من الفريق الافتراضي", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يحتاج المعنيين في مشروع رشيق إلى توضيح مستمر لقيمة أعمال المشروع.\u000bكيف يجب على مدير المشروع المضي قدما؟", description: "يقوم بتضمين العروض التوضيحية للجزء المبني من المنتج في نهاية كل تكرار\u000bسؤال مهم جدا للاختبار الحقيقى", options: [{ option: "إعداد تقرير المشروع للمعنيين وتوزيعه أسبوعيا.", answer: "false" }, { option: "يقوم بتأكيد الحد الأدنى من المنتج القابل للتطبيق واعرضه على العميل عندما يكون جاهزًا.", answer: "false" }, { option: "يقوم بتضمين العروض التوضيحية للجزء المبني من المنتج في نهاية كل تكرار", answer: "true" }, { option: "إعداد تقارير قيمة الأداء للأعمال بشكل مستمر", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يقوم فريق المشروع بإشراك فريق تطوير في بلد آخر لتطوير البرمجيات. أثناء مراجعات التقدم ، ، حدد مدير المشروع تأخيرات في تاريخ الإنجاز بسبب إعادة العمل نظرًا لوجود تفسيرات مختلفة لاحتياجات العميل. ماذا يجب أن يفعل مدير المشروع؟", description: "يقوم بإعداد اجتماع مع أصحاب المصلحة الرئيسيين لتوضيح المتطلبات", options: [{ option: "السفر إلى مكتب فريق التطوير لدفع الجدول الزمني", answer: "false" }, { option: "تصعيد إلى اللجنة التوجيهية للمشروع واطلب وقتًا إضافيًا", answer: "false" }, { option: "يقوم بإنشاء نماذج أولية مع فريق التطوير لتأكيد المتطلبات", answer: "false" }, { option: "يقوم بإعداد اجتماع مع أصحاب المصلحة الرئيسيين لتوضيح المتطلبات", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يشارك أحد أعضاء الفريق مع مديره الوظيفي بعض التفاصيل السلبية حول أداء عضو آخر في الفريق والتي تمت مناقشتها خلال اجتماع بأثر رجعي. يكتشف مدير المشروع عن هذه المناقشة. ما الإجراءان اللذان يجب على مدير المشروع اتخاذه؟ إختر إثنين.", description: "الاجابه الصحيحة :\n*تواصل مع عضو الفريق لشرح ماهية المعلومات التي يمكن مشاركتها خارج الفريق.\n*مقابلة الفريق بأكمله لمراجعة القواعد الأساسية حول البيئات الآمنة.", options: [{ option: "تواصل مع عضو الفريق لشرح ماهية المعلومات التي يمكن مشاركتها خارج الفريق.", answer: "true" }, { option: "مقابلة صاحب المنتج للتحدث عن الموقف.", answer: "false" }, { option: "مقابلة الفريق بأكمله لمراجعة القواعد الأساسية حول البيئات الآمنة.", answer: "true" }, { option: "الاتصال بالمدير الوظيفي لعضو الفريق وادعوه إلى الاجتماع التالي بأثر رجعي.", answer: "false" }, { option: "لقاء مع الراعي لمراجعة الوضع", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أنت مدير لمشروع مهم تأخر المشروع بسبب الكم الهائل من التغييرات المطلوبة من قبل مختلف أصحاب المصلحة في مراحل مختلفة من المشروع.\u000bإذا استمر المشروع على نفس المسار، فسيستهلك ضعف الوقت والميزانية المخططة. ما الإجراءان اللذان يمكن لمدير المشروع اتخاذه ليكون أكثر فعالية من حيث التكلفة؟ ", description: "الكلمة المفتاحية قدر كبير من التغييرات مطلوبة من قبل مختلف أصحاب المصلحة- لذلك فإن النهج التدريجي هو الأفضل لهذا المشروع.\nأيضًا ، يجب على مدير المشروع إجراء تحليل لأصحاب المصلحة لتحديد مصالح جميع أصحاب المصلحة ، الذين قد يؤثرون أو يتأثرون بالمشروع. المجموعات التي يجب تشجيعها للمشاركة في مراحل المشروع المختلفة. طرق لتقليل الآثار السلبية المحتملة وإدارة أصحاب المصلحة السلبيين", options: [{ option: "تحليل المعنيين", answer: "true" }, { option: "تقسيم العمل بين اعضاء الفريق", answer: "false" }, { option: "تحديث خطة إدارة المشروع مع الجدول الزمني الجديد", answer: "false" }, { option: "عزز تواصل الفريق", answer: "false" }, { option: "اعتماد نهج تدريجي", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "بدأ المشروع منذ 11 شهرًا وهو حاليا قيد التكرار 12 من 15 وقد أدى الفريق أداءً جيدًا وأكمل جميع الإنجازات في الوقت المحدد. ومع ذلك ، بدأ بعض أعضاء الفريق في الشكوى من طول مدة المشروع، مما يشكل خطرًا على قلة الأداء في هذه المرحلة الحرجة ما الذي يجب أن يفعله مدير المشروع كقائد خادم للتخفيف من\u000bهذه المخاطر؟", description: "حدد موعدًا لحدث جماعي مع راعي المشروع لتسليط الضوء على أهمية المشروع وتعزيز عمل الفريق.", options: [{ option: "حدد موعدًا لحدث جماعي مع راعي المشروع لتسليط الضوء على أهمية المشروع وتعزيز عمل الفريق.", answer: "true" }, { option: "اجتمع مع الفريق لتعزيز مسؤولياتهم والعواقب إذا لم تكتمل مخرجات المشروع في الوقت المحدد.", answer: "false" }, { option: "تطبيق ضوابط إضافية على مخرجات المشروع لضمان تحقيق جميع المعالم في الوقت المحدد.", answer: "false" }, { option: "اتصل بالمدير الوظيفي للتأكد من أن تحفيز الفريق يظل مرتفعًا حتى نهاية المشروع.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "انتهى مدير المشروع من مراجعة مخرجات المشروع للتكرار الحالي مع العميل يشتكي أحد أصحاب المصلحة الرئيسيين الجديد الذي انضم للتو إلى فريق العميل . من أن النتائج لا توفر القدرات التي يتوقعها العمل. ما الذي يجب أن يفعله مدير المشروع لمعالجة هذه المشكلة؟", description: "قبل اتخاذ أي إجراء. يجب على مدير المشروع إجراء تحديد هوية اصحاب المصلحة وجلسة مراجعة اهداف المشروع لأنه صاحب مصلحة رئيسي جديد", options: [{ option: "قم بتحديث قائمة أصحاب المصلحة وإصلاح النتائج", answer: "false" }, { option: "قم بإنشاء طلب تغيير وتحديث خطة إدارة النطاق", answer: "false" }, { option: "إجراء تحديد أصحاب المصلحة وجلسة مراجعة أهداف المشروع", answer: "true" }, { option: "تحديث خطة إدارة المشروع والدروس المستفادة", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "ينتهي المشروع وتم قبول التسليمات النهائية من قبل العميل. يسر فريق المشروع أن المشروع كان ناجحًا ومع ذلك، تعتقد بعض المجموعات داخل مؤسسة العميل أن الهدف من المشروع لم يتحقق. ما الذي كان يجب على مدير المشروع فعله لتجنب هذه المشكلة؟", description: "تم قبول التسليمات النهائية من قبل العميل ولكن بعض المجموعات داخل مؤسسة العميل تعتقد أن هدف المشروع لم يتم الوفاء به لتجنب هذا الموقف ، يجب أن يكون مدير المشروع قد تم تحديد أصحاب المصلحة الرئيسيين وإدراجهم جميعًا في مناقشات اتفاقية المشروع.", options: [{ option: "تم تنفيذ تقارير المشروع وفقًا لخطة إدارة الاتصالات", answer: "false" }, { option: "توزيع خطة مفصلة لإدارة الجودة على أصحاب المصلحة الرئيسيين", answer: "false" }, { option: "تم تضمين التقرير النهائي خلال المرحلة الختامية لضمان التفاهم المشترك", answer: "false" }, { option: "تحديد أصحاب المصلحة الرئيسيين وإدراجهم جميعًا في مناقشات اتفاقية المشروع", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يستخدم مدير المشروع نهجا مختلطا لتسليم المشروع. تم تحديد حجم فريق التطوير، ولكن لم يتم تعيين كامل فريق التطوير بعد يعمل مدير المشروع على تحديد الجدول الزمني للمشروع. ما الذي يجب على مدير المشروع فعله لمعالجة مسألة توفر المطور؟", description: "الجواب الصحيح هو :\nتنظيم جلسة تخطيط مع فريق التطوير واستخدم تقديراتهم لإكمال الجدول. يجب تقدير الأنشطة من قبل الفريق الذي يعمل على المهمة حسب سرعته لتكون أكثر دقة وواقعيه\nسؤال مهم جدا", options: [{ option: "تنظيم جلسة تخطيط مع فريق التطوير واستخدم تقديراتهم لإكمال الجدول.", answer: "true" }, { option: "استخدم حكم الخبراء لتقدير جميع المهام وإكمال الجدول الزمني في الوقت المخصص.", answer: "false" }, { option: "اطلب من الراعي تقديم تاريخ الانتهاء المطلوب لأنشطة المشروع وإكمال الجدول الزمني.", answer: "false" }, { option: "اسأل مدير الموارد عندما يكون المقاول الجديد متاحا للبدء وتغيير تاريخ التسليم لجدول المشروع.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "بعد جمع كل متطلبات مشروع له تبعيات على أصحاب المصلحة الخارجيين، ينتهي فريق المشروع من التكرار الثاني. في هذه المرحلة، يتعرف مدير المشروع على تغيير محتمل في اللوائح التي قد تمنع إطلاق المنتج. ماذا يجب أن يفعل مدير المشروع لتحقيق الفوائد؟", description: "لتحقيق الفوائد . يجب على مدير المشروع مراجعة الفوائد", options: [{ option: "تحسين سرعة الفريق", answer: "false" }, { option: "اضافة هذا التغيير إلى سجل المخاطر", answer: "false" }, { option: "مراجعة الفوائد المستهدفة", answer: "true" }, { option: "تنفيذ التغيير", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يلاحظ مدير مشروع جديد أنه من المحتمل أن يتم تفويت الموعد النهائي للمشروع بسبب حدوث بعض التأخير في المشروع بسبب نقص الموارد. كمدير مشروع ، ماذا يجب أن تفعل بعد ذلك؟", description: "الكلمة المفتاحية ( مدير مشروع جديد )\nلذا الخطوة الأولى . يجب على مدير المشروع تنظيم اجتماع للفريق لمناقشة مسار العمل التالي.", options: [{ option: "مراجعة خطة إدارة المخاطر لتحديد استراتيجية الاستجابة", answer: "false" }, { option: "تنظيم اجتماع للفريق لمناقشة مسار العمل التالي", answer: "true" }, { option: "راجع قائمة الأحداث الرئيسية لتحديد المهام التي يمكن تتبعها بسرعة", answer: "false" }, { option: "تصعيد المشكلة إلى الراعي واستخلاص المعلومات منه بشأن الموقف", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "اثناء تنفيذ المشروع، يحدث تعارض بين مدير المشروع واحد أعضاء الفريق في كل اجتماع لفريق المشروع الصراع المستمر يمنع المهام والمخرجات من الاكتمال يفكر مدير المشروع في تغيير أسلوب قيادته. فماذا يجب أن يفعل مدير المشروع؟", description: "التصعيد يكون في الحالات التالية :\nمن المحتمل أن تتسبب المشكلة التي تخطط لتصعيدها في تأخير المشروع أو تجاوز الميزانية أو تتسبب المشكلة في بذل جهد إضافي كبير من جانبك أو من جانب أعضاء فريقك لقد جربت بالفعل استراتيجيات مختلفة لإصلاح المشكلة أو احتواء المخاطر، ولكن دون جدوى لقد أبلغت الفريق المسؤول أو) عضو الفريق بالمشكلة لقد حاولت إيجاد حل مشترك مع الطرف الآخر المعني في هذه الحالة . يحدث التعارض في كل اجتماع ويمنع المهام والتسليمات من الاكتمال. لذا فإن الإجابة الصحيحة هي تصعيد الموقف إلى راعي المشروع.", options: [{ option: "تصعيد الموقف إلى راعي المشروع", answer: "true" }, { option: "مراجعة وثائق مهارات الموظفين", answer: "false" }, { option: "تجنب عضو الفريق لمنع المزيد من التعارضات", answer: "false" }, { option: "راجع مجموعة المسؤوليات مع عضو الفريق", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "بدأ مشروع اجايل والفريق المعين جديد على اجايل . خلال الاجتماعات اليومية الأولى ، لاحظ مدير المشروع أن الفريق يواجه مشاكل في تحقيق الأهداف. ماذا يجب أن يفعل مدير المشروع؟", description: "اطلب من الفريق مراجعة مناهج إدارة المشاريع الرشيقة لمعرفة ما تتم مناقشته في الاجتماع اليومي لتحقيق الأهداف.", options: [{ option: "اسأل قسم الموارد البشرية عن فريق أجايل من ذوي الخبرة", answer: "false" }, { option: "يطلب من الفريق مراجعة مناهج إدارة المشاريع الرشيقة", answer: "true" }, { option: "عقد اجتماعًا لمساعدة الفريق في تحديد ميثاق الفريق", answer: "false" }, { option: "التخطيط للانشطة خارجية للتعرف على بعضهم البعض", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أثناء تنفيذ المشروع . يراجع مدير المشروع تقدم المشروع ويجد بعض المهام التي تأخرت عن موعدها بعد التحقيق . اكتشف مدير المشروع أنه تم سحب عدد قليل من أعضاء الفريق للعمل في مهام أخرى ، مما أثر على قدرتهم على التركيز على مشروع مدير المشروع. ما الإجراءان اللذان يمكن لمدير المشروع اتخاذه الإعادة الفريق إلى المسار الصحيح؟ (إختر إثنين)", description: "", options: [{ option: "تنبيه أصحاب المصلحة على الفور بتأخير الجدول الزمني", answer: "false" }, { option: "مناقشة مخاطر تأخير المشروع وأولويات العمل مع المديرين الوظيفيين المعنيين", answer: "true" }, { option: "السماح لأعضاء الفريق باللحاق بالوثيرة التي تناسبهم", answer: "false" }, { option: "اعمل مع الفريق لتحديد الخيارات لتسريع تسليم المشروع.", answer: "true" }, { option: "قم بإنشاء قوائم انتظار يومية لمراجعة تقدم المشروع وتتبع إنجاز المخرجات", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أنت مدير مشروع تدير مشروعًا متعدد الجنسيات تمت الموافقة على خط الأساس للمشروع. أثناء تنفيذ المشروع ، اكتشف مدير المشروع ظهور العديد من المخاطر، لم يتوقع مدير المشروع هذه المخاطر خلال مرحلة التخطيط للمشروع. ما الذي يجب أن يحسبه مدير المشروع لمعالجة هذا الوضع الحرج للميزانية؟ (إختر إثنين)", description: "", options: [{ option: "مراقبة إدارة المنافع في المشروع", answer: "false" }, { option: "التحكم المتكامل في التغيير في المشروع", answer: "true" }, { option: "التحكم في التخفيف في المشروع", answer: "false" }, { option: "سعر صرف العملة", answer: "false" }, { option: "مراقبة احتياطي الطواريء في المشروع وإعادة تعيين خط الأساس", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أنت مدير لمشروع انشاءات . قام أحد المقاولين بإجراء تغييرات طفيفة على المواصفات الهندسية دون تقديم طلبات التغيير لم يناقش المقاول والفريق الفني\u000bالتغييرات مع مدير المشروع. ولا تؤثر التعديلات على ميزانية وجودة الناتج. كمدير مشروع ، ماذا يجب أن تفعل ؟", description: "يجب على مدير المشروع تحديث خطة إدارة التغيير لتحديد مستوى التحكم المطلوب لهذه الأنواع من التعديلات.", options: [{ option: "قم بتحديث خطة إدارة التغيير لتحديد مستوى التحكم المطلوب لهذه الأنواع من التعديلات.", answer: "true" }, { option: "اطلب من فريق مراقبة الجودة تقييم التعديلات. لكن لا تتخذ أي إجراء لأن مخاطر الجودة المرتبطة بها غير مهمة.", answer: "false" }, { option: "قم بالإبلاغ عن الانتهاك إلى مجلس التحكم في التغيير واطلب الموافقة الشاملة على هذه التعديلات.", answer: "false" }, { option: "اطلب من الفريق الهندسي تحديث المواصفات لتعكس التعديلات التي أجراها المقاول.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أبلغ البائع مدير المشروع أن أحد الموارد المهمة سيكون في إجازة طويلة. راجع فريق المشروع مخرجات البائع المعلقة وحدد حلا بديلا ، لكن الحل سيتكبد تكلفة إضافية. المشروع حاليا في الموعد المحدد وهو أقل قليلا من الميزانية. ماذا يجب أن يفعل مدير المشروع بعد ذلك؟", description: "الإجابة الصحيحة هي: تنفيذ التحكم المتكامل في التغيير لأن الحل سيتكبد تكلفة إضافية.", options: [{ option: "أداء التحكم المتكامل في التغيير", answer: "true" }, { option: "طلب استبدال المورد", answer: "false" }, { option: "تنفيذ خطة الاستجابة", answer: "false" }, { option: "اجراء تحليل التكلفة مقابل المنفعة", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يكافح مدير المشروع لمعرفة أداء فرق المشروع في بيئة رشيقة لنفس نطاق العمل، وقد قام الفريق أ بحساب 100 نقطة قصة والفريق ب قام بحساب 125\u000bنقطة قصة. أي فريق يعمل بشكل أفضل؟", description: "الفريق الذي يعاني من أقل قدر من العيوب في مخرجاته يعمل بشكل أفضل لأن هذا يعني اكتمال القصص واختبارها وجاهزة لاستخدام العميل.", options: [{ option: "فريق ب حيث قاموا بحساب نقاط قصة أكثر من فريق أ", answer: "false" }, { option: "الفريق \"أ\" حيث قاموا بحساب نقاط قصة أقل من الفريق \"ب\".", answer: "false" }, { option: "الفريق الذي يكمل معظم القصص المختارة من سجل اعمال التكرار", answer: "false" }, { option: "الفريق الذي لديه أقل قدر من العيوب في مخرجاته", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يتواجد الفريق في دول مختلفة حول العالم أعضاء الفريق في مناطق زمنية متعددة ويستخدمون لغات مختلفة للتواصل. يحتاج مدير المشروع إلى الحفاظ على الفريق منظما وتجنب سوء الفهم وسوء التواصل الذي قد ينتج عنه تأثير سلبي على المشروع. ما الذي يجب أن يفعله مدير المشروع لتقليل الآثار السلبية على\u000bالمشروع؟", description: "الاجابة الصحيحة :\nتعديل خطة إدارة الاتصالات لمراعاة الاختلافات الإقليمية.", options: [{ option: "عقد اجتماع افتراضي لمناقشة البيئة السياسية", answer: "false" }, { option: "تعديل خطة إدارة الاتصالات لمراعاة الاختلافات الإقليمية", answer: "true" }, { option: "قم بإجراء دورة تدريبية على الإدارة", answer: "false" }, { option: "عقد ندوات توعية ثقافية", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "بعد عدة تكرارات ، يقرر مدير المشروع تقديم المنتج إلى أحد المعنيين الرئيسيين. وكانت أحد هذه الملاحظات هي أن الفريق أخطأ في العديد من الميزات ما الذي يجب أن يفعله مدير المشروع لتجنب ذلك في المستقبل؟", description: "يجب عقد اجتماع مراجعة التكرار بعد نهاية كل تكرار لجمع التعليقات المبكرة من المعنيين وتكييف الخطة وفقًا لذلك.", options: [{ option: "إعداد جلسات جمع المتطلبات مع جميع : المعنيين الرئيسيين لتقييم النطاق مرة أخرى وإعادة ترتيب أولويات سجل أعمال المنتج بناءً على الجلسات.", answer: "false" }, { option: "جدولة اجتماع المراجعة للتكرار لجمع التعليقات المبكرة من المعنيين وتكييف الخطة وفقًا لذلك", answer: "true" }, { option: "دمج ممارسات التفكير التصميمي في دورة حياة المشروع لفهم شخصيات المنتج بشكل أفضل وتكون أكثر فاعلية لمطابقة احتياجاتهم.", answer: "false" }, { option: "تقييم مهارات الفريق للبحث عن شخص لديه خبرة في تصميم المنتج وتكليف هذا الشخص بمهمة إعادة بناء واجهة المنتج", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "تلقى مدير المشروع مؤخرًا الموافقة على تمويل برنامج تدريبي لفريق المشروع بأكمله ولكنه لم يحدد بعد الدورة التدريبية الأكثر فعالية. قدم أحد أعضاء الفريق طلب تدريب متخصص مطلوب للمشروع. كيف يجب أن يتعامل مدير المشروع مع هذا الطلب؟", description: "قبل الموافقة على الطلب أو رفضه. يجب على مدير المشروع التحقق مما إذا كان التدريب ضمن النطاق والميزانية.", options: [{ option: "الموافقة على طلب التدريب المتخصص.", answer: "false" }, { option: "رفض الطلب لأنه لا يؤثر على مخرجات المشروع.", answer: "false" }, { option: "التحقق مما إذا كان التدريب ضمن النطاق والميزانية.", answer: "true" }, { option: "أرسل طلب التغيير هذا إلى لجنة التحكم في التغيير (( CCB للموافقة عليه.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أثناء مراجعة النماذج الأولية ، وجد فريق الجودة بعض الانحرافات التي لا تؤثر على الوظيفة الرئيسية ولكن من المحتمل أن تكون مشكلة في المستقبل لتجنب المشاكل الكبيرة في المستقبل، يوصي الفريق بإجراءات تصحيحية تتطلب أموالاً إضافية. يكافح المشروع بالفعل للبقاء في الميزانية ، بل إنه استخدم بعض احتياطيات الطوارئ. ماذا يجب أن يفعل مدير المشروع؟", description: "الكلمة المفتاحية تتطلب أموالاً إضافية لذلك يجب على مدير المشروع تقييم وبدء عملية طلب التغيير مع أصحاب المصلحة. يجب أن يكون أي تغيير في خط الأساس من خلال طلب تغيير رسمي.", options: [{ option: "يسأل مصمم المنتج عن سبب عدم مراعاة الانحراف في مرحلة التصميم", answer: "false" }, { option: "يبلغ راعي المشروع أن المنتج يعمل وأن المشروع يواجه صعوبات في الميزانية.", answer: "false" }, { option: "يطلب من فريق الجودة تجاهل العينة واختبارها مرة أخرى بعينة جديدة", answer: "false" }, { option: "تقييم وبدء عملية طلب التغيير مع أصحاب المصلحة", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "مدير المشروع يقود مشروعًا مع العديد من المعنيين في المجالات الوظيفية الأخرى للشركة. تمت ترقية أحد المعنيين لقيادة مجال وظيفي آخر ، ولا يزال أحد المعنيين في هذا المشروع ما الذي يجب تحديثه لضمان التواصل المناسب معه ؟", description: "يجب تحديث سجل المعنيين لأنه يتضمن :\nمنصبه فى المؤسسة والموقع وتفاصيل الاتصال ودوره في المشروع.\nالمتطلبات الرئيسية والتوقعات وإمكانية التأثير على نتائج المشروع", options: [{ option: "خطة إدارة المشروع", answer: "false" }, { option: "سجل المعنيين", answer: "true" }, { option: "خطة مشاركة المعنيين", answer: "false" }, { option: "خطة إدارة الاتصالات", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يعمل مدير المشروع مع نفس المعنيين لمدة 4 سنوات باستخدام نهج تنبؤي. لقد استثمر المعنيين في العديد من المشاريع وهم على دراية بالنهج في الآونة الأخيرة ، شجعت المنظمة بشدة جميع مديري المشاريع على تبني نهج رشيق. تم الإعلان عن ذلك قبل أسبوعين من تخطيط مدير المشروع لبدء مشروع كبير ومعقد مع نفس مجموعة المعنيين. ماذا يجب أن يفعل مدير المشروع؟", description: "الكلمة المفتاحية (المؤسسة تشجع بشدة Agile الكلمة المفتاحية (مشروع معقد) لذا فإن الإجابة الصحيحة هي : استخدم النهج الرشيق في المشروع الجديد وجدولة تدريب رشيق للمعنيين الجدد في هذا النهج.", options: [{ option: "استخدام النهج الرشيق في المشروع الجديد وجدولة تدريب رشيق للمعنيين الجدد في هذا النهج", answer: "true" }, { option: "مشاركة ومناقشة إيجابيات وسلبيات اعتماد أجايل مع المعنيين واختيار النهج الذي يفضلونه للمشروع الجديد", answer: "false" }, { option: "مشاركة إيجابيات وسلبيات اعتماد نهج رشيق للمشروع الجديد فقط مع الفريق واطلب منهم تحديد النهج الذي يجب استخدامه", answer: "false" }, { option: "الاستمرار في استخدام النهج التنبؤي في المشروع الجديد لأنه النهج الذي يستخدمه المعنيين", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "تم تعيين مدير مشروع لمشروع التحول الرقمي لأحد البنوك. ثقافة الشركة متحفظة ومقاومة للتحول أو الابتكار، ما الذي يجب أن يفعله مدير المشروع أولاً لدعم التحول؟", description: "تقديم دورات تدريبية مناسبة للمعنيين حتى يفهموا الغرض من التغيير", options: [{ option: "تقديم دليلا للتحول الرقمي واشرح له أن التغيير لا مفر منه.", answer: "false" }, { option: "تقديم دورات تدريبية مناسبة للمعنيين حتى يفهموا الغرض من التغيير", answer: "true" }, { option: "تعيين خبيرا في التحول الرقمي وتعيينه كبطل للتغيير", answer: "false" }, { option: "اطلب من المديرين التنفيذيين للبنك أن يعلنوا رسميًا أن مدير المشروع سوف يقود المشروع.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "اثناء تطوير أحد المنتجات ، تحولت المخاطر التي تم تحديدها إلى مشكلات، مما يعيق سرعة الفريق. ماذا يجب أن يفعل مدير المشروع في هذه الحالة؟", description: "الاجابة الصحيحة هي:\nقم بإعطاء الأولوية لقصص المستخدم التي بها مشكلات واطلب من الفريق حلها.", options: [{ option: "تشكيل اللجنة التوجيهية وإبلاغها بأثر المشروع.", answer: "false" }, { option: "تحديث خطة إدارة المشروع وخطة إدارة المخاطر", answer: "false" }, { option: "ترتيب الأولوية لقصص المستخدم التي بها مشكلات وتمكين الفريق من حلها.", answer: "true" }, { option: "توثيق المشكلات وتمديد الجدول الزمني لتسليم المشروع.", answer: "false" }, { option: "يجب أن يكون فريق التطوير منظما ذاتيًا ، وموجها ذاتيا ، وأن يحل المشكلة بنفسه.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "اختارت شركة وطنية كبرى معروفة بالمنتجات عالية الجودة للتو شركة صغيرة للتعاقد من الباطن للمساعدة في مشروع يجتمع مدير المشروع من الشركة المتعاقدة من الباطن مع فريق المشروع لضمان تسليم المشروع وفقًا لمعايير العميل. من أجل ضمان ذلك، ما الذي يجب على مدير المشروع تحديده وتوثيقه؟", description: "يجب على مدير المشروع تحديد وتوثيق المتطلبات", options: [{ option: "الجودة", answer: "false" }, { option: "الاهداف", answer: "false" }, { option: "المتطلبات", answer: "true" }, { option: "التقديرات", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "مدير مشروع يقود مشروعًا قيد التنفيذ منذ عام يؤدي عضو الفريق المتمرس أداءً جيدًا . ولكنه غالبا ما يختلف مع مدير المشروع ويرفض تنفيذ قراراته. يدعم جزء من الفريق تحدي عضو الفريق ، بينما لا يدعمه أعضاء الفريق الآخرون. ماذا يجب أن يفعل مدير المشروع؟", description: "فهم ما يريده عضو الفريق والبحث عن الفرص الممكنة لمعالجته", options: [{ option: "حذف القواعد الأساسية الحالية وإنشاء قواعد أساسية جديدة تدعم مدير المشروع.", answer: "false" }, { option: "إجراء مراجعة الاداء وتحديد وتوثيق الأهداف لعضو الفريق", answer: "false" }, { option: "فهم ما يريده عضو الفريق والبحث عن الفرص الممكنة لمعالجته", answer: "true" }, { option: "توضيح القرارات واطلب من فريق المشروع اتباع قرارات مدير المشروع", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يجمع مدير المشروع أجزاء مختلفة من المعلومات ويدمج المقاييس في مشروع رشيق. يجد مدير المشروع أن هناك ركودا في الفريق من حيث الإنتاجية، انخفض عدد العناصر المرسلة إلى التطوير لكل شخص بنسبة 10% . واستخدم مدير المشروع تحليل السبب الجذري ((RCA) واكتشف أن السبب هو نقص المعرفة في أحد الأطر المستخدمة للنمذجة. ماذا يجب أن يفعل مدير المشروع بعد ذلك؟", description: "قبل البحث عن خبير خارجي والترتيب للتدريب الخارجي. يجب على مدير المشروع أولا تعزيز تبادل المعرفة والتدريب بين أعضاء الفريق.", options: [{ option: "إضافة أعضاء جدد للفريق ضعيف الأداء.", answer: "false" }, { option: "البحث عن خبير خارجي لمراجعة إطار العمل", answer: "false" }, { option: "الترتيب للتدريب الخارجي وسط الجداول الزمنية الضيقة للمشروع.", answer: "false" }, { option: "تعزيز تبادل المعرفة والتوجيه بين أعضاء الفريق.", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يتم تعيين مدير المشروع لفريق المشروع في منتصف الطريق خلال التنفيذ تؤثر العديد من التبعيات على المشروع ويشعر مدير المشروع أن المعنيين على غير علم كاف بالتغييرات المؤثرة ما الذي يجب أن يفعله مدير المشروع أولاً؟", description: "مراجعة خطة إدارة الاتصالات.", options: [{ option: "مراجعة سجل المعنيين.", answer: "false" }, { option: "تصعيد المشكلة لمالك المنتج.", answer: "false" }, { option: "مراجعة خطة إدارة الاتصالات.", answer: "true" }, { option: "عقد اجتماع تحليل التأثير", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "مدير مشروع يدير مشروع هجين . أثناء تنفيذ المشروع، تم إبلاغ مدير المشروع أنه تمت إعادة تخصيص أكثر من نصف ميزانية المشروع لمشروع يتعامل مع حالة طوارئ حدثت مؤخرا. ماذا يجب أن يفعل مدير المشروع؟", description: "مقابلة مالك المنتج لتأكيد الخيار الأدنى للمنتج القابل للتطبيقMVP \nالخيار )لقاء مع فريق المشروع وإعادة ترتيب سجل اعمال المنتج (غير صحيح لأن هذه مسؤولية مالك المنتج.\nالخيار اللقاء مع راعي المشروع لشرح أنه لا يمكن إكمال المشروع كما هو مخطط له غير صحيح لأن مالك المنتج يمكنه تأكيد خيار الحد الأدنى من المنتج القابل للتطبيق MVP\nوالحد الأدنى من المنتج القابل للتطبيق MVP هو إصدار من منتج يحتوي على ميزات كافية فقط ليتم استخدامها من قبل العملاء الذين يمكنهم بعد ذلك تقديم ملاحظات للمستقبل ومع MVP ، جميع أصحاب المصلحة لديهم فرصة لرؤية وتجربة شكل من أشكال نتائج المشروع.", options: [{ option: "مقابلة القائد الفني لمراجعة خطة التكرار.", answer: "false" }, { option: "الاجتماع راعي المشروع لتوضيح أن المشروع لا يمكن أن يكتمل كما هو مخطط له", answer: "false" }, { option: "الاجتماع مع فريق المشروع وعادة ترتيب أولويات سجل اعمل المنتج.", answer: "false" }, { option: "قابل مالك المنتج لتأكيد الخيار الأدنى للمنتج القابل للتطبيق MVP", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "مدير مشروع يدير مشروعًا رشيفًا لمدة 6 أشهر خلال التكرارين الأخيرين ، انخفضت سرعة الفريق بسبب التكنولوجيا الجديدة التي كانت مطلوبة للمنتج. ما الذي يجب على مدير المشروع فعله للاستمرار في خطة التسليم والعودة إلى السرعة المتوقعة؟", description: "استخدم اجتماع بأثر رجعي لفهم تأثير التكنولوجيا الجديدة على الجدول الزمني بشكل أفضل، ورسم خريطة للفجوات في المهارات ، وضبط الفريق وفقا لذلك", options: [{ option: "اعد تقييم سجل اعمال المنتج من خلال البحث عن العناصر ذات القيمة العالية / منخفضة الجهد والتفاوض بشأن استراتيجية الحد الأدنى من المنتجات القابلة للتطبيقMVP مع أصحاب المصلحة الرئيسيين", answer: "false" }, { option: "استخدم اجتماع بأثر رجعى لفهم تأثير التكنولوجيا الجديدة على الجدول الزمني بشكل أفضل. ورسم خريطة للفجوات في المهارات، وضبط الفريق وفقًا لذلك", answer: "true" }, { option: "اطلب موافقة الراعي للحصول على تمويل إضافي لتعيين أعضاء جدد للفريق، وزيادة قدرة الفريق وتعويض الإنتاجية المفقودة مع التكنولوجيا الجديدة.", answer: "false" }, { option: "استبدل أعضاء الفريق ذوي الأداء الضعيف بأعضاء أكثر خبرة ، ليكون مثالاً يحتذى به في مساءلة الفريق", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "انضم مدير مشروع مؤخرًا إلى شركة صغيرة. طلب رئيس العمليات في الشركة من الفريق تخطي بعض الخطوات في العملية من أجل العمل بشكل أسرع. يريد مدير المشروع\u000bالتأكد من عدم تخطي هذه الخطوات في المستقبل. ماذا يجب أن يفعل مدير المشروع؟", description: "نظرا لأن مدير المشروع جديد يجب عليه أولاً مراجعة خطوات العملية مع الفريق ومدير العمليات.", options: [{ option: "حدد موعدا لمراجعة خطوات العملية مع الفريق ومدير العمليات.", answer: "true" }, { option: "أعد تصميم العملية حتى يتمكن الفريق من التحرك بسرعة أكبر", answer: "false" }, { option: "تصعيد المسألة لأعضاء القيادة التنفيذية الآخرين.", answer: "false" }, { option: "قم بإخطار جميع أعضاء الفريق بأنه يجب إكمال جميع خطوات العملية.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "منظمة تتبنى عقلية رشيقة. في أول مشروع رشيق واجه مدير المشروع مشكلة لأن الفريق لم يكن قادرا على اتخاذ القرارات في الوقت المحدد ما الذي يجب أن يفعله مدير المشروع لمعالجة المشكلة؟", description: "قم بتقييم الموقف لفهم كيف تؤثر ثقافة المنظمة على عملية صنع القرار وقم بتدريب الفريق نحو نموذج جديد.", options: [{ option: "تحديد مبادئ توجيهية واضحة حول كيفية اتخاذ القرارات في إطار السياسة التنظيمية الجديدة وتعزيزها مع الفريق في كل مناسبة.", answer: "false" }, { option: "اعتماد أسلوب قيادة استبدادي وإملاء جميع القرارات من أجل تسريع سرعة الفريق.", answer: "false" }, { option: "ادع القيادة العليا إلى ورشة عمل لبناء الفريق وأعد التأكيد على أهمية المساءلة والقيادة في ظل التوجيه الجديد للشركة.", answer: "false" }, { option: "قم بتقييم الموقف لفهم كيف تؤثر ثقافة المنظمة على عملية صنع القرار وقم بتدريب الفريق نحو نموذج جديد.", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يدير مدير المشروع مشروعًا تقنيا يتطلب توثيقا مكثفًا كجزء من التسليمات الرئيسية في اجتماع مراجعة المشروع، أبلغ أحد المعنيين الرئيسيين أن أحدث الوثائق التي قدمها مدير المشروع تتضمن إشارة إلى المنتجات غير ذات الصلة. ماذا يجب أن يفعل مدير المشروع؟", description: "الإجابة الصحيحة هي: تطوير معيار لمراقبة جودة المستندات", options: [{ option: "قم بتسجيل المشكلة في سجل المخاطر وسجل المشاكل", answer: "false" }, { option: "تعيين كاتب فني للمشروع", answer: "false" }, { option: "اطلب من مكتب إدارة المشروع دعم ومراجعة جميع التسليمات", answer: "false" }, { option: "وضع معيار لمراقبة جودة المستندات", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "تولى مدير المشروع للتو مشروعًا في مرحلة تجميع المتطلبات. يشير أحد المعنيين إلى أنهم لم يتلقوا أي تحديثات بشأن التقدم على الرغم من إرسال التقارير على أساس أسبوعي. ما الذي يجب أن يفعله مدير المشروع للتأكد من أن جميع المعنيين يتلقون المعلومات المناسبة حول المشروع؟", description: "يجب على مدير المشروع تقييم جميع المعنيين في المشروع وتوثيق طريقة الاتصال المفضلة لديهم وتكرارها.", options: [{ option: "انشئ تقارير تقدم يومية وأرسلها إلى جميع المعنيين", answer: "false" }, { option: "تقييم جميع المعنيين في المشروع وتوثيق طريقة الاتصال المفضلة لديهم وتكرارها", answer: "true" }, { option: "إشراك المعنيين في اجتماعات الحالة الأسبوعية مع الفريق", answer: "false" }, { option: "عقد اجتماع لجنة توجيهية مع جميع المعنيين لتقديم تحديث شامل عن تقدم المشروع", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "مدير مشروع على وشك الانتهاء من مشروع الأتمتة. يطلب أحد المعنيين الرئيسيين عقد اجتماع لمناقشة بعض الأفكار الجديدة التي من شأنها أن تجعل المشروع أكثر جاذبية للمستخدمين النهائيين. نتيجة لذلك، يقترح المعنيين أن مدير المشروع لا يغلق المشروع. ما الذي يجب أن يفعله مدير المشروع أولا؟", description: "الكلمة الرئيسية )يقترح المعنيين) لذا فهو خطر وليست مشكلة.\nيجب على مدير المشروع أولاً إضافة الخطر إلى سجل المخاطر ثم تحليل المخاطر لمعرفة التأثير.", options: [{ option: "يقوم بإضافته إلى سجل المشكلات باعتباره مشكلة مستمرة", answer: "false" }, { option: "تحديث ميثاق المشروع بالمتطلبات الجديدة", answer: "false" }, { option: "مراجعة والتحقق من صحة خطة إدارة النطاق", answer: "false" }, { option: "إضافة الخطر إلى سجل المخاطر", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "في اجتماع اللجنة التوجيهية للمشروع . آثار عدد قليل من المعنيين مخاوف من أن تقرير مدير المشروع يوضح أن نتيجة المشروع موجودة بالفعل في السوق بينما لا يزال المشروع قيد التطوير يوضح مدير المشروع أن المشروع يتم تسليمه على تكرارات ويتضمن إصدارات مختلفة. ما الذي يجب على مدير المشروع فعله لتجنب حدوث هذا السيناريو؟", description: "يتأكد أن محتوى تقرير المشروع يكون مفهوم بوضوح ويطلب أي ملاحظات ضرورية", options: [{ option: "إرسال تقرير مفصل إلى كل عضو من أعضاء اللجنة التوجيهية قبل الاجتماع", answer: "false" }, { option: "يقوم بدعوة المعنيين المهمين إلى اجتماعات مراجعة المشروع اليومية لضمان مشاركتهم الكاملة ومشاركتهم", answer: "false" }, { option: "يتأكد أن محتوى تقرير المشروع يكون مفهوم بوضوح ويطلب أي ملاحظات ضرورية", answer: "true" }, { option: "تأكد من اكتمال جميع ميزات المنتج وفقًا للمواصفات المبلغ عنها قبل طرحها في السوق", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "بدأ مدير المشروع مشروع جديد وتم تعيين أدوار ومسؤوليات الفريق. استعدادا لتطوير خطة إدارة المشروع، يقوم مدير المشروع بمراجعة الدروس المستفادة من المشاريع السابقة ويكتشف أن نفس الفريق قد فاته مواعيد التسليم. ما الذي يجب أن يفعله مدير المشروع لضمان التسليم في الوقت المناسب؟", description: "الاجابة الصحيحة هي تحديث سجل المخاطر لانها ليست مشكله في المشروع الحالي ولكن كانت مشكله في المشاريع السابقة ، فيجب على مدير المشروع تسجيلها كخطر وتحليلها وعمل خطة استجابه للخطر.", options: [{ option: "تصعيد المشكله إلى المدير الوظيفي", answer: "false" }, { option: "اطلب من قائد الفريق تخصیص موارد أخرى.", answer: "false" }, { option: "تحديث سجل المشاكل", answer: "false" }, { option: "تحديث سجل المخاطر", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "خلال اجتماع يهدف الى تحديد نطاق المشروع ، يبلغ أحد المعنيين مدير المشروع بأن الميزه الرئيسية المطلوبة فى العقد غير قابلة للتطبيق ، وقد تم توثيق ذلك في الدروس المستفادة. ما الذي ينبغى على مدير المشروع فعله ؟", description: "يجب على مدير المشروع مراجعة الفريق لمعرفة ما يمكن القيام به ، واستشارة خبير", options: [{ option: "التحقق مما إذا تم توثيق هذه المعلومات بشكل صحيح", answer: "false" }, { option: "مراجعة الفريق لمعرفة ما يمكن القيام به ، واستشارة خبير", answer: "true" }, { option: "مقابلة راعى المشروع لطلب الدعم منه", answer: "false" }, { option: "مطالبة راعى المشروع باغلاق المشروع ودفع الغرامة المتعاقد عليها", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "اشتكى بعض الاعضاء الاكثر خبرة في فريق المشروع من مدير المشروع . فأعضاء فريق المشروع الاقدم يشعرون بأن مدير المشروع يتدخل في كل الامور الصغيرة ويمنعهم من إكمال عملهم . ما الذي ينبغي على مدير المشروع فعله بعد معرفته بهذه المشاكل ؟", description: "إعداد حدث لبناء روح الفريق من أجل إعادة التعاون بين أعضاء فريق المشروع", options: [{ option: "إعداد حدث لبناء روح الفريق من أجل إعادة التعاون بين أعضاء فريق المشروع", answer: "true" }, { option: "تعديل اسلوب مدير المشروع في الادارة ليلائم أعضاء الفريق الاقدم بشكل أفضل", answer: "false" }, { option: "مقابلة كل عضو قديم في الفريق بصفة يومية لمتابعة حالة المشروع", answer: "false" }, { option: "طلب المشورة حول كيفية توجيه أعضاء الفريق هؤلاء بشكل أفضل", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يستمر مالك المنتج منذ بداية المشروع فى السؤال خلال الاجتماعات حول الموازنة التى تتفق لكل تكرار للمنتج . ويبدو أن مالك المنتج مهتم أكثر بالتكلفة بدلا من المنتج نفسه .ماذا كان ينبغى على مدير المشروع فعله سابقا لتغيير سلوك مالك المنتج ؟", description: "العمل مع مالك المنتج لتوضيح دوره فى مشروع الاسلوب الرشيق اجايل ونطاق اجتماعات الاسلوب الرشيق لان دور مدير المشروع ف اجايل قائد خادم وداعم ويلتزم التاكد ان الجميع يطبق اجايل بالطريقه الصحيحة", options: [{ option: "العمل مع مالك المنتج لتوضيح دوره فى مشروع الاسلوب الرشيق اجايل ونطاق اجتماعات الاسلوب الرشيق", answer: "true" }, { option: "العمل على عقد السعر الثابت لتحويل انتباه مالك المنتج الى القيمة بدلا من المال", answer: "false" }, { option: "العمل على اساليب تقدير مختلفة لبث الثقة بالتكلفة التى تنفق فى كل إضافة للمنتج", answer: "false" }, { option: "العمل على خطة إدارة التواصل مع تقارير الموازنة التى تنفق فى كل تكرار مقابل التخطيط لتجنب هذه المناقشات اثناء الاجتماعات", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "حدد أحد اعضاء فريق المشروع عيبا في تصميم الحل الأولى للمشروع . إن المعنيين غير متأكدين مما اذا كان ينبغى نشر النظام أو لا كمدير مشروع ماذا يجب ان تفعل ؟", description: "اعتماد استراتيجية تخفيف المخاطر استنادا إلى تصميم الحل", options: [{ option: "مراجعة قياسات ضبط جودة المشروع", answer: "false" }, { option: "اقتراح إعادة تصميم الحل للمشروع", answer: "false" }, { option: "قم بإجراء تحليل للمخاطر بشأن هذه المشكلة.", answer: "false" }, { option: "اعتماد استراتيجية تخفيف المخاطر استنادا إلى تصميم الحل", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "تم تعيين مدير مشروع جديد لتولى مشروع معرض لخطر الفشل . لا يعلم المعنيون شيئا عن حالة المشروع او المنافع التي سيقدمها . كيف يمكن لمدير المشروع نقل هذه المنافع إلى المعنيين ؟", description: "إجراء تحليل التكلفة مقابل المنفعة الخاص بالمشروع للمعنيين\nلذا يجب على مدير المشروع إجراء تحليل التكلفة مقابل المنفعة على مدار المشروع لمساعدة اللجنة المسؤله عن المشروع على اتخاذ القرارات الصحيحة ولضمان تسليم الفوائد وفقًا لخطة إدارة الفوائد. الخيار (إنشاء خطة إدارة المنافع وفقا للأهداف الإستراتيجية للمؤسسة) غير صحيح لأنه يجب تطوير هذه الخطة قبل المشروع.", options: [{ option: "إجراء تحليل التكلفة مقابل المنفعة الخاص بالمشروع للمعنيين", answer: "true" }, { option: "تحديث خريطة التسليمات بالمقارنة مع متطلبات المشروع المتفق عليها", answer: "false" }, { option: "إنشاء خطة إدارة المنافع وفقا للاهداف الاستراتيجية للمؤسسة", answer: "false" }, { option: "إعادة تحديد الخط المرجعي للجدول الزمنى للمشروع لاظهار حالة المشروع الحالية", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "زادت النزاعات داخل الشركة بعد مرور عام من تنفيذ تغيير فى السياسة لتحسين التنوع والشمولية داخل فريقين. ما الذي ينبغى فعله لمنع هذا النزاع ؟", description: "كان ينبغى توفير تدريب عن التنوع للعاملين على نطاق المؤسسة", options: [{ option: "كان ينبغى على المؤسسة عرض مفهوم التنوع بشكل اشمل", answer: "false" }, { option: "كان ينبغى توفير تدريب عن التنوع للعاملين على نطاق المؤسسة", answer: "true" }, { option: "كان ينبغى ان تشارك الادارات المهمة فى المؤسسة في هذه الممارسة", answer: "false" }, { option: "كان ينبغى على فريق القيادة العمل بشكل وثيق مع فريق التسويق للاطلاع على التنوع", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "إذا أراد مدير المشروع التأكد من أن العمل المنجز والمنتج الذي يتم إنشاؤه لا ينحرف عن قيمة العمل المحددة. ما الذي يجب أن يوليه مدير المشروع اهتمامًا خاصًا لتحقيق هذا الهدف؟", description: "يجب أن يولي مدير المشروع اهتماما خاصا لمراقبة خطة تحقيق المنافع وتحديثها للتأكد من أن العمل المنجز والمنتج الذي يتم إنشاؤه ولا ينحرف عن قيمة الأعمال المحددة خطة تحقيق المنافع : وثيقة تحدد الأنشطة الضرورية لتحقيق المنافع المخططة. يحدد الجدول الزمني والأدوات والموارد اللازمة لضمان تحقيق الفوائد بالكامل بمرور الوقت. يحدد الفوائد والافتراضات المرتبطة بها ، وكيف سيتم تحقيق كل فائدة\nسؤال مهم للاختبار الدولي", options: [{ option: "مراجعة وتحديث خطة إدارة المتطلبات", answer: "false" }, { option: "مراقبة هيكل تجزئة العمل WBS", answer: "false" }, { option: "إجراء عمليات تدقيق الجودة وتحليل الفشل", answer: "false" }, { option: "مراقبة وتحديث خطة تحقيق المنافع", answer: "true" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "تم تفويض مدير المشروع لبدء المشروع ويدرك أن هناك حاجة إلى فريق فعال وعالي الأداء لإنهاء المشروع بنجاح ماذا يجب أن يفعل مدير المشروع بعد ذلك؟", description: "حدد نطاق المشروع وحدد أهدافًا واضحة للفريق", options: [{ option: "حدد الأعضاء لمهاراتهم أو إمكاناتهم", answer: "false" }, { option: "حدد نطاق المشروع وحدد أهدافًا واضحة للفريق", answer: "true" }, { option: "ضع معايير الأداء والاتجاه", answer: "false" }, { option: "عقد اجتماعات انطلاق مبكرة واجتماعات أسبوعية منتظمة", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يلاحظ مدير المشروع أن هناك مشاركة ومساهمة محدودة من عضو جديد في الفريق. ماذا يجب أن يفعل مدير المشروع؟", description: "يجب على مدير المشروع أولاً الانضباط - التصحيح - التوجيه) عضو الفريق الجديد بسبب مشاركته ومساهمته المحدودة. الخيار (مراجعة أدوار ومسؤوليات كل عضو في الفريق. غير صحيح لأن مدير المشروع يلاحظ أن هناك مشاركة ومساهمة محدودة من عضو جديد في الفريق فقط وليس كل أعضاء الفريق. يجب أن يكون مدير المشروع استباقيًا لذلك الخيار (ناقش الموقف مع راعي المشروع. اكتب شكوى إلى قسم الموارد البشرية) وليس الممارسات الجيدة كخطوة أولى", options: [{ option: "كتابة شكوى إلى قسم الموارد البشرية.", answer: "false" }, { option: "توجيه وتصحيح عضو الفريق الجديد.", answer: "true" }, { option: "مناقشة الموضوع راعي المشروع.", answer: "false" }, { option: "مراجعة أدوار ومسؤوليات كل أعضاء فريق المشروع .", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "ما الإجراءان اللذان يجب على مدير المشروع مراعاتهما أثناء تعيين فريق افتراضي جديد؟ اختر اجابتين", description: "الاجابة الصحيحة :\nحدد في البداية كيف سيتم رصد التقدم وأفضل الوسائل لإبلاغ التقدم\nالتحقق مما إذا كان أعضاء الفريق في أكثر من فريق ولا يمكن تخصيص الوقت الكافي لهذا الفريق\nسؤال مهم جدا للاختبار الحقيقي", options: [{ option: "حدد في البداية كيف سيتم رصد التقدم وأفضل الوسائل لإبلاغ التقدم", answer: "true" }, { option: "يقوم يتعيين أفضل الموارد داخل المنظمة لتكون جزءًا من الفريق الافتراضي", answer: "false" }, { option: "تحقق مما إذا كان أعضاء الفريق في أكثر من فريق ولا يمكن تخصيص الوقت الكافي لهذا الفريق", answer: "true" }, { option: "تحديد أنواع وكميات الموارد المطلوبة لكل حزمة عمل أو نشاط", answer: "false" }, { option: "يقوم بتقييم نقاط القوة والضعف لدى مدير المشروع كفائد افتراضي للفريق لتحديد العوائق التي يمكن تجنبها", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "بدأ مشروع مؤخرًا. يلاحظ مدير المشروع أن هناك توقعات مختلفة بين أعضاء فريق المشروع. يؤثر سوء الفهم المتزايد هذا على إنتاجية الفريق. ما المستند الذي يجب أن يستخدمه مدير المشروع كدليل لحل المشكلة؟", description: "ميثاق الفريق: مستند يمكّن الفريق من إرساء قيمه واتفاقياته وممارساته أثناء أدائه لعمله معًا.\u000bيتضمن ميثاق الفريق الجيد ما يلي:\u000b- القيم المشتركة للفريق.\u000b- مبادئ توجيهية لاتصالات الفريق واستخدام الأدوات.\u000b- كيف يتخذ الفريق القرارات.\u000b- كيف يحل الفريق الخلافات عند ظهور الخلافات.", options: [{ option: "ميثاق الفريق", answer: "true" }, { option: "سجل المعنيين", answer: "false" }, { option: "ميثاق المشروع", answer: "false" }, { option: "مخطط تنظيم المشروع", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يريد العميل أن يُظهر لأصحاب المصلحة نتيجة ملموسة بشكل أسرع. هذا يتطلب المضي قدما في معلم. أي شيئين يجب على مدير المشروع القيام به؟ (إختر إثنين)", description: "مراجعة أولويات العميل فيما يتعلق بالمتطلبات المطلوبة للمعالم. \nمناقشة المتطلبات التي يمكن تسليمها بشكل أسرع مع فريق المشروع.", options: [{ option: "مناقشة المتطلبات التي يمكن تسليمها بشكل أسرع مع فريق المشروع.", answer: "true" }, { option: "تصعيد المشكلة للإدارة العليا حتى يتمكنوا من فرض حل على العميل.", answer: "false" }, { option: "مراجعة أولويات العميل فيما يتعلق بالمتطلبات المطلوبة للمعالم.", answer: "true" }, { option: "أبلغ العميل أن نقل المرحلة الرئيسية له تأثير كبير جدًا على تكلفة المشروع.", answer: "false" }, { option: "اقنع العميل بالحفاظ على المتطلبات كما هي وعدم تحريك المرحلة.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يقود مدير المشروع مشروعًا سيتم تسليمه باستخدام إطار عمل سكرم. تمتلك المنظمة مكتبًا مبتكرًا لإدارة المشاريع يدعم الادارة الرشيقة بشكل كامل ولديها أيضًا فريق مشتريات متعاون للغاية. يحتاج البائع الجديد إلى المشاركة لتوفير المزيد من المطورين لمنصة تقارير جديدة ماذا يجب أن يفعل مدير المشروع؟", description: "يجب إشراك البائع الجديد وفقًا لنهج رسمي باستخدام سياسات المشتريات والإجراءات والنماذج وتعليمات العمل.", options: [{ option: "استخدم نهجًا رشيعًا وقم بإشراك مورد حقق أداءً جيدًا في مؤسسة سابقة", answer: "false" }, { option: "استخدم نهجًا رسميًا باستخدام سياسات المشتريات والإجراءات والنماذج وتعليمات العمل", answer: "true" }, { option: "استشر مالك المنتج وقم بإشراك البائع الذي أوصى به", answer: "false" }, { option: "استشر فريق التطوير وقم بإشراك البائع الذي أوصى به", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "في مشروع يستخدم نهجا هجينا ، كان أداء الفريق ممتازا باستمرار خلال المراحل التنبؤية. ومع ذلك، عندما بدأت التكرارات ، بدأ أعضاء الفريق في إظهار علامات تدني الالتزام والصراعات والارتباك ، مما أدى إلى انخفاض الروح المعنوية، ما الذي يجب أن يفعله مدير المشروع للتعامل مع هذا الموقف؟", description: "الكلمة المفتاحية ( عندما بدأت التكرارات. بدأ أعضاء الفريق في إظهار علامات تدني الالتزام والصراعات والارتباك (ولذلك يجب على مدير المشروع تدريب أعضاء الفريق على العمليات والمعرفة بالطرق الرشيقة", options: [{ option: "أداء أنشطة بناء الفريق وتعزيز التعاون", answer: "false" }, { option: "تدريب أعضاء الفريق على العمليات والمعرفة بالطرق الرشيقة", answer: "true" }, { option: "امنح أعضاء الفريق مزيدًا من السلطة لاتخاذ القرارات.", answer: "false" }, { option: "حدد أعضاء الفريق الذين يؤثرون على معنويات منخفضة.", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "يختلف اثنان من المديرين الوظيفيين حول السمات الرئيسية لأحد التسليمات أثناء التخطيط للمشروع يكتشف مدير المشروع أن كل مدير يتضمن متطلبات تتعارض مع بعضها البعض. لا يرغب المديرون الوظيفيون في الاجتماع مع بعضهم البعض لإيجاد حل. تم تصعيد هذا الموقف إلى الراعي الذي يطلب الاجتماع معهم ما هي الإستراتيجية التي يجب أن يستخدمها مدير المشروع لحل هذا التعارض في الاجتماع؟", description: "تأكد من أن كلا المديرين يفهمان المتطلبات والبحث عن حل يلبي هذا التسليم على أفضل وجه", options: [{ option: "اطلب من المديرين شرح سبب وجوب تنفيذ كل متطلب واتخاذ القرار من خلال النظر في التكلفة والفوائد", answer: "false" }, { option: "تأكد من أن كلا المديرين يفهمان المتطلبات والبحث عن حل يلبي هذا التسليم على أفضل وجه", answer: "true" }, { option: "اشرح للمديرين أنه يمكن تنفيذ واحد فقط من المتطلبين ، وقدم طلب تغيير ،", answer: "false" }, { option: "اشرح للمديرين أنه يتم النظر في المتطلبات المتناقضة للتنفيذ في المشروع", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "أنت مدير مشروع في شركة ومشروعك في مرحلة التنفيذ حاليا. لاحظت أن تقارير أداء العمل تدهورت بشكل كبير في أقل من أسبوعين. يشكو فريق المشروع في البلد \"أ\" من التأخيرات الناتجة عن الإجازات التي تحدث في البلد \"ب\" والتي أثرت على فريق المشروع. كيف يجب أن تتعامل مع هذا الوضع؟", description: "يجب على مدير المشروع إجراء إدارة الصراع باستخدام خطة إدارة موارد المشروع للتعامل مع هذا الموقف.", options: [{ option: "تنفيذ لضغط الجدول الزمنى باستخدام crashing وتحسين مؤشر أداء الجدول SPI", answer: "false" }, { option: "تطبيق إدارة الصراع باستخدام خطة إدارة موارد المشروع.", answer: "true" }, { option: "راجع خطة إدارة المخاطر لتقييم احتمالية وتأثير هذه التأخيرات.", answer: "false" }, { option: "تنفيذ التتبع السريع لضغط الجدول وتحسين SPI", answer: "false" }] }, { type: "mcq", degree: 0, chapter: "", domain: "", name: "تم إضافة عضو فريق جديد إلى فريق جيد التنظيم. يتردد عضو الفريق الجديد في التحدث والمشاركة في مناقشات الفريق أو القرارات. ما الإجراءان اللذان\u000bيجب على الفريق اتخاذه لإشراك عضو الفريق الجديد هذا؟ إختر إثنين", description: "الإجابة الصحيحة هي تدريب عضو الفريق الجديد على تحسين التفاعل مع الفريق وأعد النظر في مناقشة الفريق حول سلوكيات الفريق ومعاييره. الخيار: (إجبار عضو الفريق الجديد والإبلاغ عن مدير المشروع) ليس ممارسات جيدة.", options: [{ option: "مراجعة مناقشات الفريق حول سلوكيات الفريق ومعاييره.", answer: "false" }, { option: "إجبار عضو الفريق الجديد على المشاركة في الأنشطة التي تعتبر سهلة.", answer: "false" }, { option: "تسهيل مناقشة مفتوحة لجميع الأفكار ووجهات النظر.", answer: "false" }, { option: "تدريب عضو الفريق الجديد على تحسين التفاعل مع الفريق.", answer: "true" }, { option: "أبلغ مدير المشروع عن سلوك عضو الفريق الجديد.", answer: "false" }] }]